style.css:
:root {
    
    --base-light: 45%;
    --base-hue: 300;
    --base-sat: 46%;
    --base-opacity: .75;
    
    --darker-color: hsl(var(--base-hue), calc(var(--base-sat) - 42%), calc(var(--base-light) - 32%));
    --dark-color: hsla(calc(var(--base-hue) - 10), 20%, calc(var(--base-light) - 27%), var(--base-opacity));
    --darker2-color: hsl(calc(var(--base-hue) - 35), 18%, calc(var(--base-light) - 30%));
    --darkerish-color: hsla(calc(var(--base-hue) - 10), 8%, calc(var(--base-light) - 32%), calc(var(--base-opacity) + .183));
    --dark2-color: hsla(calc(var(--base-hue) - 60), 30%, calc(var(--base-light) - 27%), calc(var(--base-opacity) + .03));
    
    
    --interest: hsla(calc(var(--base-hue) - 64), 19%, calc(var(--base-light) - 23%), calc(var(--base-opacity) + .2));
    --interest-hover: hsla(calc(var(--base-hue) - 64), 19%, calc(var(--base-light) - 7%), calc(var(--base-opacity) + .25));
    --input-color: hsla(calc(var(--base-hue) - 50), 19%, calc(var(--base-light) - 27%), calc(var(--base-opacity) - .201));
    
    
    --link-color: hsla(calc(var(--base-hue) - 90), 40%, calc(var(--base-light) + 11%), calc(var(--base-opacity) + .25));
    --link-color-hover: hsla(calc(var(--base-hue) - 85), 95%, calc(var(--base-light) - 2%), calc(var(--base-opacity) + .25));
    --link-color-visited: hsla(calc(var(--base-hue) - 60), 30%, 40%, 0);
    
	--light-color: #454545;
	--light-color-mid: #555;
	--light-color-code: #777;
    --lighter-color: hsl(calc(var(--base-hue) - 67), 42%, calc(var(--base-light) + 17%), calc(var(--base-opacity) + .25));
    
    --border-color: hsla(calc(var(--base-hue) - 106), 13%, 60%, calc(var(--base-light) + 10%));
    --scroll-fg-color: hsla(calc(var(--base-hue) - 50), 32%, calc(var(--base-light) - 13%), calc(var(--base-opacity) + .25));
    --scroll-bg-color: hsla(calc(var(--base-hue) - 50), 22%, calc(var(--base-light) - 25%), .5);
    
    
    --button-color: hsla(calc(var(--base-hue) - 105), 80%, calc(var(--base-light) - 20%), calc(var(--base-opacity) + .02));
    --button-hover: hsla(calc(var(--base-hue) - 105), 80%, calc(var(--base-light) - 14%), calc(var(--base-opacity) + .221));
    /* --button-color2: hsla(183, 27%, 28%, .7);*/
    --button-color2: hsla(calc(var(--base-hue) + 40), 27%, calc(var(--base-light) - 25%), .7);
    
    --text-color: #ccc;
	--text-color-light: #ddd;
    --text-dim: #777;
    --text-bright: #ddd;
    --text-placeholder: hsla(213, 42%, 67%, calc(var(--base-opacity) + .25));
    
    /*--darker-color: #48454f;*/
    --semilight-color: #3b4c60;
    
    --svg-fill: hsla(calc(var(--base-hue) - 100), 52%, calc(var(--base-light) - 5%), calc(var(--base-opacity) + .15));
    
    
    --success-border: #5cb85c;
    --success-bg: #4bc34b69;
    --success-bg-solid: #4bc34beb;
    
    --error-border: #ae0f0f;
    --error-bg: #38030324;
    --error-bg-solid: #380303eb;
    
    --warning-border: #aeae0f;
    --warning-bg: #38380324;
    --warning-bg-solid: #383803eb;
    
    --info-border: #0f47ae;
    --info-bg: #031e3824;
    --info-bg-solid: #031e38eb;
    --info2-bg: #034563;
    
	--alert-color: #435f82;
	--shade-color: rgba(30, 30, 30, 0.8);
    
}
