/*----------СТРАНИЦА АВТОРИЗАЦИИ----------*/

/*фон страницы*/
#innerLayout {
	background: fixed var(--bg-sur-1);
	background-size: cover;
}
/*фон основного блока*/
.login-page {
	padding: 0;
	border: none;
	background: none;
}
/*убрать круг на фоне страницы*/
.login-page:before {
	border: none;
}
/*окно авторизации*/ 
.login-box {
	overflow: hidden;
	background: rgba(245, 245, 245, 0.8);
	background-blend-mode: hard-light;
	backdrop-filter: blur(40px);
	border-radius: 10px;
	box-shadow: var(--shadow-8);
}
/*вкладки*/
.login-tabs__navigation ul {
	box-shadow: none;
}
.login-tabs__navigation li a.active {
	background: var(--primary-white);
}
.login-tabs__navigation li a:before {
	background: none;
}
/*контент*/
.login-tabs__content {
	background: var(--primary-white);
}
.login-tabs__content > li {
	padding: 32px;
}
/*кнопки в окне авторизации*/
.login-button {
	border-radius: 6px;
	box-shadow: none;
}
/*кнопка Facebook в окне авторизации*/
.login-button--facebook {
	background: rgba(0, 0, 0, 0.05);
}
/*кнопка ВОЙТИ в окне авторизации*/
.login-button--submit {
	background: var(--primary-blue);
}
/*поля ввода логина и пароля*/
.login-form [type="text"], .login-form [type="password"], .login-form [type="number"] {
	border: 1px solid #D1D1D1;
	border-radius: 6px;
}
/*фон футера*/
.login-page ~ #footer-container {
	background: transparent;
}
#footer-container .footer-bottom {
	background: rgba(245, 245, 245, 0.5);
	background-blend-mode: luminosity;
	backdrop-filter: blur(40px);
	border-top: 0;
}
