	padding: 10px;
	background: rgba(245, 245, 245, 0.5);
	background-blend-mode: luminosity;
	backdrop-filter: blur(40px);
	border-radius: 10px;
	box-shadow: var(--shadow-4);
}
.suggestsearchmain {
	overflow: hidden;
	padding: 0;
	border: 0 !important;
}
/*элемент списка подсказок поиска*/
.suggestsearchmain li a.title {
	border: none;
	/*box-shadow:0px 1px 0px #CCC;*/
}
.suggestsearchmain li:last-child a.title {
	box-shadow: none;
}
.suggestsearchmain li a.title:hover, .suggestsearchmain li a.search-choose-selected {
	background: var(--primary-blue);
	border-radius: 6px;
}
/*список выбора региона*/
.regions-layer {
	overflow: hidden;
	padding: 10px;
	background: rgba(245, 245, 245, 0.5);
	background-blend-mode: luminosity;
	backdrop-filter: blur(40px);
	border: 0;
    border-radius: 15px;
    box-shadow: var(--shadow-4);
}
