.regions-layer .title {
	margin: 0;
	padding: 0;
	border-bottom: none;
	text-align: center;
}
.regions-layer .title:hover {
	background: var(--primary-blue);
	border-radius: 6px;
}
#regionSearchBox > a,
#subregionSearchBox > a {
	display: block;
	padding: 14px;
	text-decoration: none;
	font-weight: bold;
}
#regionSearchBox:hover > a {
	color: var(--white);
}
/*колонки с регионами*/
.regions-layer .column {
	padding: 0;
	border-left: none;
}
/*элемент списка регионов*/
.regions-layer .column li a {
	padding: 12px;
	text-decoration: none;
}
.regions-layer .column li a:hover {
	background: var(--primary-blue);
	border-radius: 6px;
}
.regions-layer .column li a span:after {
	top: 15px;
}

