style.css:
@-moz-document domain("lubimyczytac.pl") {
/* OKNO DODAWANIA KSIĄŻKI BO BIBLIOTEKI */

.modal {
  margin-top: 20px;
}
/* szerokość dialoga */
.modal-dialog {
  max-width: 700px;
}
/* ukrycie przycisku "Ustaw alert"*/
.add-book-to-library__alert {
  display: none;
}
/* nazwa książki */
.modal .modal-header h5 {
  font-size: 1.1rem;
}
/* przyciski "zapisz" / "zamknij" */
.btn-secondary, .btn-primary {
  padding: 5px 15px;
  margin-top: 0px !important;
  margin-bottom: 0px !important;
  font-size: 0.7rem;
}
/* przyciski zwykłych półek*/
.btn--bookshelf {
  font-size: 0.75rem;
  padding: 0.2rem .55rem .2rem;
  margin: 2px !important;
}
/* slider z głównymi półkami*/
.modal .sliderHtml .sliderHtml-wrapper {
   padding-bottom: 0px;
   gap: 0px !important;
}
/* element slidera z głównymi półkami */
.modal .sliderHtml-slide {
  width: fit-content !important;
  margin: 0px !important;
  flex: 0.1 0.1 auto !important;
}
/* przyciski z głównymi półkami */
.modal .read-action {
  padding: 0px !important;
  margin-top: 0px !important;
  margin-bottom: 20px !important;
  height: 70px !important;
  width: 110px !important;
}
/* input daty przeczytania */
.edit-shelf-hidden .form-control--library {
  height: 25px;
}
/* formularz wyszukiwania półek */
.preStyledForm__form {
  margin-left: 5px;
}
/* input wyszukiwania półek */
.preStyledForm__input {
  line-height: 0px;
  font-size: 0.7rem;
  padding-bottom: 0px !important;
}
/* div z kafelkami półek */
.modalAddToLib__another-shelves {
  max-height: 350px;
}
/* napisy "Dodaj na półkę", "Twoja ocena" */
.mb-1, .m2-n2 {
  margin-bottom: 0px !important;
  font-size: 0.9rem;
}
/* wystawiona książce ocena */
.modal .rating-value .big-number {
  font-size: 2rem;
}
/* link do opini video */
.modal div.form__field {
  display: none;
}

/* ========== */

/* REKLAMY */

/* ukrycie reklam (top) */
.ads__TopCollContainer~.header {
    top: 0px;
}
.isTopAdsFixed .header {
  top: 0px;
}
div#js-AdsTopCollContainer {
  top: 0px;
  height: 0px;
}

/* ========== */

/* WARSTWA WYSZUKIWANIA (kolor tła + rozmycie + położenie)*/
.overlay--search {
  background-color: #F2F2F2;
  padding-left: 0px;
  padding-right: 0px;
  padding-bottom: 5px;
  top: calc(var(--header-height));
}
.suggestions {
  padding-left: 32px;
  padding-right: 32px;
}
.suggestions::after {
  background-image: none;
}
.overlay--search::after {
    content: "";
    height: 220px;
		background-image: linear-gradient(180deg,transparent,hsla(0,0%,100%,.8) 50%,#FFF);
    width: 100%;
    position: -webkit-sticky;
    position: sticky;
    left: 0;
    bottom: -32px;
    display: block;
    margin-top: -191px;
    pointer-events: none;
}

/* STRONA GŁÓWNA */
/* ukrycie sekcji z aktualnościami */
section.container > div.listing {
  display: none;
}

/* zmiana koloru tła */
main, .is-search-open .header__wrapper, header.header, div.searchbox, div.header__wrapper, div.header__secondary, div.header__primary,  section.container-fluid, section, .lcaward-banner, div.calendar, li.list-group-item, .chart .progress, div#reviewsList, div#reviewsListPaginatorButton, .bg-white, div#listOfArticlesPaginator, div#listOfArticles, .listFiltered-hld,  .book-menu, .book-menu .btn, .dashBoardAccount__intro {
  background-color: #F2F2F2 !important; /* #F2F2F2 */ /* #ECECEC */
}
.book .book-cover {
  border-color: #F2F2F2;
}
.buy-box .bookstores h3 {
  background: #F2F2F2;
  border: #b9b9b9 1px solid;
}

/* zmiana marginesów (powodują białe tło) */
div.lcaward-banner, div.calendar, section, section.challenge, section.latest-articles,  section.latest-activites, section.carousel.carousel--news-and-previews, section.squiggle, section.carousel, section.carousel.carousel--patronages, section.popular-books, section.favorite-quotes {
  margin-top: 1px;
  margin-bottom: 1px;
  padding-top: 5px;
  padding-bottom: 5px;
}

/* usunięcie efektu pływających sekcji */
.latest-articles__float .tile, .favorite-quotes__float .quote-tile, .reviews-tile--featured {
  position: static;
}

/* dodanie obrazka z tłem dla pływających sekcji (po usunięciu "pływania" tło zniknęło)*/
.latest-articles__float article.tile .tile__wrapper {
	background-image:url("https://s.lubimyczytac.pl/rwd/src/assets/images/kafelek-tresci-tlo.webp");
	object-fit: cover;
}

/* dodanie kolorów tła dla pływających sekcji (po usunięciu "pływania" tło zniknęło)*/
.tile--category-4 div.tile__wrapper{
	background-color: #006b99;
  opacity: 0.95;
}
.tile--category-8  div.tile__wrapper{
	background-color: #201361;
  opacity: 0.95;
}
.tile--category-9  div.tile__wrapper{
	background-color: #188578;
  opacity: 0.95;
}
.tile--category-12  div.tile__wrapper{
	background-color: #931665;
  opacity: 0.95;
}
.tile--category-13  div.tile__wrapper{
	background-color: #930;
  opacity: 0.95;
}
}
