    background-color: #444aa7;
}

.puzzle__session a.current {
    background: #445aa7;
}


.cmn-toggle:checked {
    color: #445aa7;
}

.user-link.online .line {
    color: #509cd6;
}

.community__leaders.leaderboards {
    border: 3px solid #445aa7;
    border-top-color: #445aa7;
    border-right-color: #445aa7;
    border-bottom-color: #445aa7;
    border-left-color: #445aa7;
    border-top-width: 3px;
    border-right-width: 3px;
    border-bottom-width: 3px;
    border-left-width: 3px;
    border-top-style: solid;
    border-right-style: solid;
    border-bottom-style: solid;
    border-left-style: solid;
    border-image-source: initial;
    border-image-slice: initial;
    border-image-width: initial;
    border-image-outset: initial;
    border-image-repeat: initial;
}

.community__leaders .leaderboards .user-top {
    border-right: 3px solid #4457ac;
    border-right-width: 2px;
    border-right-style: solid;
    border-right-color: #4457ac;
}

.community__leaders .leaderboards h2 {
    background: #445aa7;
    border-right: 3px solid #4457a7;
    border-left-color: #445aa7;
    border-right-width: 2px;
    border-right-style: solid;
    border-right-color: #4457ac;
}

.cmn-toggle:checked + label {
    background-color: #445ca7;
}

square.last-move {
    background-color:  #69c;
}

.fbt {
    text-transform: uppercase;
    line-height: 1.5;
    transition: all 150ms;
}
@media (hover: hover) {
    .fbt:hover:not(.disabled):not([disabled]) {
        background: #445ca7;
        color: #fff;
    }
}
.fbt.active {
    background: #445ca7 !important;
    color: #fff;
}

.is-green::before {
    color: #446ca7;
}

.button.button-empty.button-green,
.button.button-empty.button-green:hover {
    color: #445ca7;
}

.user-link.online .line {
    color: #226994;
    opacity: 0.9;
}

#friend_box .content a.user-link .line::before {
    color: #226994;
    content: "";
}

signal > i {
    width: 20%;
    margin-left: 1px;
    display: inline-block;
    height: 40%;
    background-color: #445ca7;
}

.pocket {
    background: #ccc;
}

}
