style.css:
@-moz-document url-prefix("https://sktorrent.eu")
{
/*------------------------------------------------------------*/
/*background-color*/
body {
  background-color: #2d2d2d !important;
  background-size: 0%;
  width: 100%;
  height: 100%;
  height: auto !important;
  background-repeat: no-repeat;
  background-position: center;
  margin-top: 1.3em !important;
  padding: 0;
  justify-content: center;
  font-family: "Garamond", "arial", "tahoma", "sans-serif", "helvetica" !important;
}
/*horni lista - home-----------*/
td.header {
  color: white;
  width: 940px !important;
  width: auto !important;
  margin: auto !important;
  margin-left: 5px !important;
  font-size: 8.5pt !important;
  padding: 7px;
  margin: 0px;
  border: 1pt solid black;
  border-radius: 2px;
  box-shadow: 0.5px 0.5px 0.5px 0.5px rgba(255, 0, 0, 0.3),
    -0.5px -0.5px 0.5px 0.5px rgba(255, 0, 0, 0.3) !important;
  background: #4f4f4f !important;
  background-image: -moz-linear-gradient(
    90deg,
    rgba(54, 53, 53, 0.3) 0%,
    rgba(80, 80, 80, 0.1) 25%,
    rgba(93, 93, 93, 0.1) 50%,
    rgba(143, 143, 143, 0.1) 83%,
    rgba(159, 159, 159, 0.15) 94%
  ) !important;
  background-image: -webkit-linear-gradient(
    90deg,
    rgba(54, 53, 53, 0.3) 0%,
    rgba(80, 80, 80, 0.1) 25%,
    rgba(93, 93, 93, 0.1) 50%,
    rgba(143, 143, 143, 0.1) 83%,
    rgba(159, 159, 159, 0.15) 94%
  ) !important;
  filter: contrast(140%);
}
/*lišta------hladaj-----fulltext-----nadpisy butt-color------------*/
td.block {
  width: 940px !important;
  width: auto !important;
  color: white !important;
  font-weight: bold;
  border: 1px solid #000 !important;
  background: #4f4f4f !important;
  background-image: -moz-linear-gradient(
    90deg,
    rgba(54, 53, 53, 0.3) 0%,
    rgba(80, 80, 80, 0.1) 25%,
    rgba(93, 93, 93, 0.1) 50%,
    rgba(143, 143, 143, 0.1) 83%,
    rgba(159, 159, 159, 0.15) 94%
  ) !important;
  background-image: -webkit-linear-gradient(
    90deg,
    rgba(54, 53, 53, 0.3) 0%,
    rgba(80, 80, 80, 0.1) 25%,
    rgba(93, 93, 93, 0.1) 50%,
    rgba(143, 143, 143, 0.1) 83%,
    rgba(159, 159, 159, 0.15) 94%
  ) !important;
  filter: contrast(130%) !important;
  box-shadow: 0.5px 0.5px 0.5px 0.5px rgba(255, 0, 0, 0.3),
    -0.5px -0.5px 0.5px 0.5px rgba(255, 0, 0, 0.3) !important;
  border: 1.7px solid #1f1f1f !important;
  filter: brightness(90%);
  border-radius: 2px;
  padding: 0px;
  margin: 0px;
  text-align: center;
}
/* personal panel */
td.header:nth-child(1) {
  background-color: #4f4f4f !important;
}
/*personal panel + velky border--list-------------*/
td.lista {
  background: #5e5e5e !important;
  border: 2px solid #616161 !important;
}
/*border--------------------------------------*/
table.lista {
  position: relative;
  display: inline-table;
  max-width: 940px !important;
  width: 940px !important;
  height: auto !important;
  margin-left: 0px auto !important;
  margin-right: 0px auto !important;
  border: 1.5px solid #f6f6f6 !important;
  left: 0 !important;
  right: 0 !important;
  border-radius: 4px;
  box-shadow: 0.5px 0.5px 0.5px 0.5px rgba(255, 0, 0, 0.6),
    -0.5px -0.5px 0.5px 0.5px rgba(255, 0, 0, 0.3) !important;
  background: #707070 !important;
  background-image: -moz-linear-gradient(
    0deg,
    rgba(54, 53, 53, 0.7) 0%,
    rgba(80, 80, 80, 0.7) 25%,
    rgba(93, 93, 93, 0.6) 50%,
    rgba(143, 143, 143, 0.6) 83%,
    rgba(159, 159, 159, 0.3) 94%
  ) !important;
  background-image: -web-linear-gradient(
    0deg,
    rgba(54, 53, 53, 0.7) 0%,
    rgba(80, 80, 80, 0.7) 25%,
    rgba(93, 93, 93, 0.6) 50%,
    rgba(143, 143, 143, 0.6) 83%,
    rgba(159, 159, 159, 0.3) 94%
  ) !important;
  background-image: linear-gradient(
    0deg,
    rgba(54, 53, 53, 0.7) 0%,
    rgba(80, 80, 80, 0.7) 25%,
    rgba(93, 93, 93, 0.6) 50%,
    rgba(143, 143, 143, 0.6) 83%,
    rgba(159, 159, 159, 0.3) 94%
  ) !important;
}
/*hlavni venkovni border*/
.lista {
  height: auto !important;
  margin-left: auto;
  margin-right: auto;
  font-size: 8.5pt !important;
  padding: 1px 1px 1px 1px !important;
  border: 1.5px solid #1f1f1f !important;
}
