style.css:
@-moz-document domain('iformbuilder.zendesk.com'), url-prefix('https://iformbuilder.zendesk.com/') {
  body {
        background-color: #3e4042;
        color: #333333;
        font-family: 'Helvetica Neue', Arial, Helvetica, sans-serif;
        font-size: 15px;
        font-weight: 400;
        line-height: 1.5;
        -webkit-font-smoothing: antialiased;
    }

    .article-list-item a {
        color: #e6e6e6;
        font-family: helvetica;
        font-size: 14px;
        font-weight: bold;
    }

    .search-result-description {
        margin-top: 15px;
        color: white;
    }
    .meta-data {
      color: #e6e6e6;
      font-size: 13px;
      font-weight: 300;
    }
     .article-content {
        line-height: 1.6;
        margin: 40px 0;
        word-wrap: break-word;
        color: white;
    }
  .article-votes {
    border-top: 1px solid #ddd;
    padding: 30px 0;
    text-align: center;
    color: white;
}
  .article-more-questions {
    margin: 10px 0 20px;
    text-align: center;
    color: white;
}
  .article-return-to-top a {
    color: #30aabc;
    display: block;
    padding: 20px 0;
}
  .article-meta {
    display: inline-block;
    margin-left: 10px;
    vertical-align: middle;
    color: #739f3c;
}
  .article-body p em font {
    color: #ffeb3b;
}
  .breadcrumbs li a:visited {
    color: #30aabc;
}
  .breadcrumbs li {
    color: #89bf48;
    display: inline;
    font-weight: 300;
    font-size: 13px;
    max-width: 450px;
    overflow: hidden;
    text-overflow: ellipsis;
}
  .comment-callout {
    color: #e6e6e6;
    display: inline-block;
    font-weight: 300;
    font-size: 13px;
    margin-bottom: 0;
}
  .comment-meta {
    flex: 1 0 auto;
    color: #30aabc;
}
  .comment-published {
    color: #e6e6e6;
    display: block;
    font-weight: 300;
    font-size: 13px;
}
  .comment-body {
    -moz-hyphens: auto;
    -ms-hyphens: auto;
    -webkit-hyphens: auto;
    word-break: break-word;
    font-family: 'Helvetica Neue', Arial, Helvetica, sans-serif;
    line-height: 1.6;
    color: white;
}
.page-header h1 {
    flex-grow: 1;
    font-size: 22px;
    margin-bottom: 10px;
    font-family: "helvetica";
    color: #54772a;
    font-weight: bold;
}
  .page-header-description {
    padding-top: 20px;
    font-style: normal;
    font-weight: 300;
    margin: 0 0 30px 0;
    word-break: break-word;
    color: #e6e6e6;
    font-weight: bold;
    font-family: "helvetica";
}
   .wysiwyg-font-size-small {
    font-size: large;
}
    .wysiwyg-font-size-small strong font {
    color: #30aabc;
}
  
  .wysiwyg-color-black {
    color: #e6e6e6;
}
  .footer a {
    color: #30aabc;
}
}
