style.css:
@-moz-document domain("messenger.com") {
/* Messenger Dark Theme v1.0*/
/* Created by frokfrdk*/

/* Dark Scrollbar*/
::-webkit-scrollbar,
 ::-webkit-scrollbar-corner,
 ::-webkit-scrollbar,
::-webkit-scrollbar-corner {
    background: var(--bg) !important;
    width: 8px
  }
::-webkit-scrollbar-button,
 ::-webkit-scrollbar-button {
    display: none !important;
  }
::-webkit-scrollbar-thumb,
 ::-webkit-scrollbar-thumb {
    background: #999 !important;
    width: 8px !important;
    border-radius: 10px;
     transition: .1s !important;
  }
::-webkit-scrollbar-thumb:not(:active):hover,
 ::-webkit-scrollbar-thumb:not(:active):hover {
    background: #777 !important;
     transition: .1s !important;
  }
::-webkit-scrollbar-thumb:active,
::-webkit-scrollbar-thumb:hover:active,
 ::-webkit-scrollbar-thumb:active,
 ::-webkit-scrollbar-thumb:hover:active {
    background: #5c5c5c !important;
     transition: .1s !important;
  }

/*Variables*/
    :root {
        --bg: #000;
        --bg2: #141414;
	    --bg3: #222;
        --item: #333;
        --item-hover: #666;
        --item-active: #777;
        --text: #fff;
        --text-dark: #ddd;
        --text-dark2: #aaa;
        --none: #00000000;
        --debug: #0f0;
}

.__fb-light-mode{
        --surface-background: #000;
        --messenger-card-background: #000;
        --card-background: #141414;
        --comment-footer-background: #141414 !important;
        --web-wash: #000 !important;
        --fds-gray-20: var(--debug);
        --divider: #333;
        --media-inner-border: #181818;
        --hosted-view-selected-state: #222;
        --popover-background: #222;
        --wash: #333;
        --secondary-button-text: #fff;
        --secondary-button-background: #222 !important;
        --secondary-text: #aaa;
        --overlay-alpha-80: #0000;
        --primary-icon: #fff;
        --always-black: #fff;
        --primary-text: #fff;
        --filter-primary-icon: invert(100%);
}

/*Main*/
body{
    background: var(--bg) !important;
}

/*Sidebar*/

/*Message List*/
.xgqcy7u:hover{
    background-color: var(--bg3);
    transition: .03s;
}

/*Facebook Marketplace*/
.x1qhmfi1 /*view product button*/{
    background-color: var(--bg3)
}

div.x78zum5.xdt5ytf.x5yr21d /*product info background*/{
    background-color: var(--bg2);
    border-radius: 15px;
}

/*Speech bubble menus*/
.x1jx94hy{
    background-color: var(--bg2);
}

.x1y1aw1k:hover{
    background-color: var(--bg3);
    transition: .04s;
    border-radius: 15px;
}

/*Pop up menu*/
.x104qc98{
    box-shadow: 0px 0px 7px rgba(0,0,0,0.5);
}
}
