    background: black !important;
    border-radius: 5px;
    box-shadow: 1px 1px 5px #444 !important
}
#mvhead,
header,
#header,
footer,
#footer {
    background: black !important;
}
body > #dialog,
body > .xenOverlay {
    background: black !important;
    background-clip: padding-box !important;
    box-shadow: 0 0 15px #000, inset 0 0 0 1px rgba(200, 200, 200, .5), inset 0 0 5px black !important;
    border: 10px solid rgba(99, 99, 99, .7) !important;
    border-radius: 0 !important;
}
[id*="overlay"],
[id*="lightbox"],
blockquote {
    background-color: black !important;
    border-radius: 5px
}
pre,
dl,
.Message code {
    background-color: black !important
}
/*----- DEFAULT BUTTONS, SEARCHBOXES & CO -----*/
input,
select,
a.button,
.a-button-input,
.a-dropdown-common .a-dropdown-item:active .a-dropdown-link,
.a-dropdown-common .a-dropdown-item:hover .a-dropdown-link,
.a-last,
a.submit,
a.BigButton,
a.TabLink,
.install[onclick] {
    background: #000 !important;
    color: pink !important;
    border: 1px solid orange !important;
}
button {
    border: 1px solid darkgrey !important;
    color: orange !important;
}
[role="button"] {
    border: 0.8px solid darkorange !important;
    color: orange !important;
}
a[href="javascript:;"],
a[class*="button"]:not(:empty),
a[id*="button"]:not(:empty),
a[id*="Button"]:not(:empty),
div[class*="button"][onclick] {
    transition: border-color 0.3s !important;

    color: lime !important;
    border-color: orange !important;
}




input *,
a-buton-input *,
select *,
button *,
a.button *,
a.submit * {
    color: coral ! important;
    background-color: black !important;
    text-shadow: none !important
}

input:focus,
select:focus {
    box-shadow: 0 0 5px #077 !important
}

input[disabled],
select[disabled],
button[disabled],
input[disabled]:hover,
select[disabled]:hover,
button[disabled]:hover,
input[disabled]:focus,
select[disabled]:focus,
button[disabled]:focus {
    opacity: 0.5 !important;
    border-color: #333 !important
}
/*
input[type="checkbox"], input[type="radio"] {     box-shadow: 0 0 0 2px #444, 0 0 2px 2px #000 !important;     opacity: 0.7;     transition: box-shadow 0.2s, opacity 0.2s !important}
input[type="checkbox"]:not([disabled]):hover, input[type="radio"]:not([disabled]):hover {     opacity: 0.9}
input[type="checkbox"]:not([disabled]):active, input[type="radio"]:not([disabled]):active {     box-shadow: 0 0 0 2px #999, 0 0 2px 2px #000 !important}
input[type="checkbox"]:checked, input[type="radio"]:checked {     box-shadow: 0 0 0 2px #077, 0 0 2px 2px #000 !important}
input[type="checkbox"][disabled], input[type="radio"][disabled] {     opacity: 0.35}
*/
input[type="checkbox"] {
    border-radius: 1px !important
}
input[type="radio"],
input[type="radio"]:focus {
    border-radius: 100% !important
}
input[type="checkbox"],
input[type="radio"] {
    min-width: 12px;
    min-height: 12px
}
input[type="checkbox"]:checked,
input[type="radio"]:checked {
    border-color: #1ebcbc !important;
    box-shadow: 0 0 5px #21b9b9 !important
}
select {
    padding-right: 15px !important;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAcAAAAECAYAAABCxiV9AAAAAXNSR0IArs4c6QAAAARnQU1BAACxjwv8YQUAAAAJcEhZcwAADsMAAA7DAcdvqGQAAAAeSURBVBhXY5g5c+Z/BiwALo6uAEMDTACXSWgSDAwA4jATh950E30AAAAASUVORK5CYII=) no-repeat right 4px center black !important;
}

button:active,
input[type="submit"]:active,
input[type="button"]:active,
a.button:active,
a[class*="button"]:not(:empty):active,
a.submit:active,
a.BigButton:active,
a.TabLink:active,
.Active .TabLink {
    background: #000 !important;
    color: #FFF !important
}
textarea,
textarea * {
    color: #EEFFBA!important;
    background-color: black !important;
}



textarea:focus {

    border-color: #7a7a7a !important
}
textarea:focus,
textarea:focus > * {
    text-shadow: none !important;
    box-shadow: none !important
}
option,
optgroup {
    -moz-appearance: none !important;
    -webkit-appearance: none !important;
    background: none !important;
    color: #666 !important
}
optgroup {
    background: black !important;
    color: #DDD !important
}
option:not([disabled]):hover,
option:focus,
option:checked {
    background: linear-gradient(#333, #292929) !important;
    color: #DDD !important
}
