@-moz-document url-prefix("https://sktorrent.eu") {
/*-------------------------------------------------------------------------------------------------------------*/

/*blocker -- remove text, imgs---------------------------------------------------------------------------------*/

/*remove back-col BASE theme - FIX*/
 table {
	background-color: transparent !important;
	height: 0 !important;
}
/*dark theme - fix - top*/
 td {
	height: 0 !important;
}
img[src="/torrent/images/winter/graphics-winter-494254.gif"] {
	display: none !important;
	width: 0px !important;
	height: 0px !important;
}
img[src="./style/dark/torrent_logo.png"] {
	display: none !important;
	height: 0px !important;
}
img[src="http://announce.sktorrent.eu/torrent/style/CzT/conteiner.gif"] {
	display: none !important;
	width: 0px !important;
	height: 0px !important;
}
font[style="font-size:750%; font-face:Verdana; color:#0489B1; text-shadow:black 3px 3px 4px;"] {
	font-size: 0px !important;
	top: -1% !important;
}
font[style="font-size:200%; font-face:Verdana; color:#0489B1; text-shadow:black 1px 1px 1px;"] {
	font-size: 0px !important;
}
font[style="font-size:250%; font-face:Verdana; color:#0489B1; text-shadow:black 1px 1px 1px;"] {
	font-size: 0px !important;
}
tr[style="background-image:url(http://announce.sktorrent.eu/torrent/style/CzT/header_trans_cz.png)"] {
	display: none !important;
}
td[style="background-image:url(./style/CzT/header_trans.png)"] {
	background: none !important;
	height: 0% !important;
}
tr[style="background-image:url(./style/CzT/conteiner.gif)"] {
	background: transparent !important;
	width: 100%;
	height: 0% !important;
	margin: 0%;
	background-size: 0px;
	background: none !important;
}
table[style="background-image:url(./style/CzT/conteiner.gif)"] {
	background: transparent !important;
	width: 0%;
	height: 0% !important;
	margin: 0%;
	background-size: 0;
}
center > table > tbody > tr > td > table {
	display: none;
}
}

