@-moz-document domain("sktorrent.eu") {
/*-----------------------------------------------------------------------*/

/*horni lista - home-----------*/
 td.header {
    color: var(--hlavnipismo) !important;
    width: 940px !important;
    width: auto !important;
    margin: 0 auto !important;
    margin-left: 5px !important;
    font-size: 8.5pt;
    padding: 7px !important;
    border: var(--bordertoplista);
    outline: none;
    border-radius: var(--radiustoplista);
    box-shadow: var(--shadowtoplista) !important;
    background: var(--backtoplista) !important;
    filter: contrast(140%);}

/*lišta------hladaj-----fulltext-----nadpisy butt-color------------*/
 td.block {
    width: 940px !important;
    width: auto !important;
    color: var(--hlavnipismo) !important;
    font-weight: bold;
    filter: contrast(130%) !important;
    filter: brightness(90%);
    border-radius: 200px;
    padding: 0px;
    margin: 0px;
    text-align: center;
    backdrop-filter: blur(1px);
}
/* personal panel */
 td.header:nth-child(1) {
 background: rgba(0, 0, 0, .55) !important;}

/*personal panel + velky border--list-------------*/
 td.lista {
    background-color: var(--seznamplocha) !important;
    backdrop-filter: blur(1px);
    border-radius: 2px;
   }

/*border--------------------------------------*/
 table.lista {
    display: inline-table;
    max-width: 940px !important;
    width: 940px !important;
    height: auto !important;
    margin-left: 0px auto !important;
    margin-right: 0px auto !important;
    border: none !important;
    left: 0 !important;
    right: 0 !important;
    /*-border-radius: 4px;-*/
}

/*hlavni venkovni border*/
