style.css:
@-moz-document domain(sololearn.com) {
html, body {
     background: #333!important;
}
.materialInput input, h1, h2, h1 a, h1, .sl-description-text,  .sl-rich-text li, .le-course__header__title, .le-course-widget__title, .sl-user-heart__heart-count, .le-module__title, .sl-single-choice-item, .recentArticles ul li a, .archives ul li a, .articleContent p, .courseItem a:hover .courseDescription div, .courseItem a:hover .courseDescription h1, .codeBlock .tooltip, .noteBlock .tooltip, #replies .answer .postDetails .message, #replies .answer .postDetails .message, .postDetails .message, .takeCourse p, .courseReviewTitle:not(.sl-code-coach-editor) {
color: #eee!important;
}
span {

}
#leaderboard .leaderboardList a {
    color: #abf;
}
.sl-rich-text p, .sl-rich-text h3, .sl-rich-text h2, .sl-rich-text h1 {
  color: #eee
}
 #output,
#leaderboard .rank {
     color: #ddd;
}
#quizSelector {
    color: #c2c2c2;
}
 #textView #textContent, .userAchievements .achievement .details {
     color: #C2C2C2!important;
}
 .noCode .description {
     color: #C2C2C2!important;
}
 .userCourses .courseXp {
     color: #eee!important;
}
 .userAchievements .achievement .title {
     color: #eee!important;
}
 .questionTabs .tabs li a {
     color: #eee!important;
}
 .questionTabs .tabs li a:hover{
     background-color: #666!important;
    ;
}
.sl-le-cc-tabs {
  background-color: #141618!important;
    border: 1px solid #141618!important;
}
.sl-le-cc-tabs__item--active {
    color: #ffffff;
}
#comments .commentContainer, .sl-cc-solution__body {
    background: #414141;
}
#comments .comment {
    border-left: solid 3px #2297b7;
    border-bottom: solid 1px #333333;
    border-top: solid 1px #8e8e8e;
}

#comments .comment .name {
    color: #ddd;
}
#comments .comment .text {
      color: #aaa;
}
 select {
     background: #222!important;
}
 .materialButton.primary.elevated.addQuestion {
     background-color: #444!important;
}
.placeholderDragAndDropQuiz .answers {
    margin-top: 20px;
    background: #165f73;
    box-shadow: 0 1px 3px rgba(0,0,0,.12), 0 1px 2px rgba(0,0,0,.24);
}
 .questionTabs .tabs li.active a {
     color: #1abc9c;
     background-color: #5f5f5f;
     border: solid 1px #ff8282!important;
}
 .authorDetails .date {
     font-size: 12px;
     color: #B1B1B1;
}
 #textView .noteBlock {
     border: solid 1px #ff8282!important;
     background: #AB3101!important;
}
 .courseDescription p {
     color: #b9b9ba!important;
}
 .courseDescription div, .courseDescription h1 {
     color: #C9D6F5!important;
}
 .postDetails .title a {
     color: #E8E4E4!important;
}
 .postVotes, .postAnsewers {
     color: #FC6565!important;
}
 .block {
     background-color: #212121!important;
}
 .codeTabs .tabs li a:hover {
     background-color: #757575!important;
}
 .codeTabs .tabs li.active a {
     color: #1abc9c!important;
     background-color: #404040!important;
     border: 1px solid #ff6e6e;
}
 #app, #textView .textContentContainer {
     background-color: #414141!important;
}
 .courseModule, .lessonVideo, .lessonChecks {
     background-color: #000!important;
}
 .shortcutContent .shortcut {
     background-color: #eb6654!important;
     color: #ddd!important;
}
 #quizView .quizQuestion {
     color: #DBD6D6!important;
}
 .multipleChoiceQuiz .answer, .strikeOutQuiz .answer, .sl-single-choice-item.initial {
     border-top: solid 1px #665f5f!important;
     background: #323335!important;
}
 #textView .codeBlock {
     border: solid 1px #626262!important;
     background: #212121!important;
}
 .navigation-menu ul li a, .slider a, .courseStores a {
     background-color: #0c6855!important;
}
 .appLesson.disabled {
     background: #3b3b3b !important;
}
 #socialElements, .reorderQuiz .answer, .appLesson, .footerSocial {
     background-color: #222!important;
}
 #footer, .le-lesson-footer {
     background-color: #000!important;
}
.sl-lesson__content {
  background: #181515;
}
.sl-description-note {
	background: #493605!important;
}
.sl-quiz-textfield, .sl-quiz-question__answer-text {
