}
.sl-quiz-comments-container__body, .sl-quiz-comments-container__header {
background: #181515;
}
.sl-quiz-answer-wrapper, .le-lesson-wrapper {
background:#292929!important;
}
.sl-lesson-footer{
  background:#1e1e1e;
