}
.sl-cc-regular-item, .sl-lesson-item.completed, .le-course-widget, .le-course-widget.completed, .sl-lesson-item.locked, .sl-lesson-item.unlocked {
  background:#2b2b2b!important;
}
.sl-rich-text code {
    background-color: #292929;
}
.sl-lesson-item__index{
  color:#eeeeee!important;
}
.sl-single-choice-item.selected {
    background: #14507b!important;
}
