:root {
  --theme: var(--p5-primary);

  /* New Main Color */
  --p5-primary: #cc0001;
  --hp-green: #00ddb2;
  --mp-pink: #ff66ed;
  --p5-blue: #40c1fb;
  --white: #ebebeb;

  /* Standard Variables */

  --primary-dark: #131313;
  --primary-alt: #111;

  /* Backgrounds */
  --background-primary: #101010;
  --background-secondary: var(--primary-alt);
  --background-secondary-alt: var(--primary-alt);
  --background-accent: var(--theme);
  --background-tertiary: var(--theme);

  /* Channel Text Area */
  --channeltextarea-background: var(--white);

  /* Scrollbar */
  --scrollbar-thin-thumb: var(--theme);
  --scrollbar-auto-thumb: var(--theme);
  --scrollbar-auto-track: #222;

  /* Header */
  --header-primary: var(--white) !important;

  /* 9A0002 */

  /* Text */
  --text-link: var(--theme);
  --text-muted: var(--white);
  /* --text-muted: #cc0001; */

  /* Focus */
  -focus-primary: var(--white);
}
.dark{
  --incoming-background: transparent;
  --outgoing-background: transparent;
  --panel-header-background: var(--primary-dark);
  --conversation-panel-background: var(--p5-primary);
  --conversation-panel-background: transparent;
  --background-default: var(--primary-dark);
  --intro-background: transparent;

  --compose-panel-background: transparent;
  --rich-text-panel-background: transparent;
  --compose-input-background: transparent;
  --compose-input-border: transparent;


  --icon: var(--primary-dark);

  /*Chat text*/
  --input-placeholder: #4e4e4e;
  /*--primary: var(--primary-dark);*/
  /*Background chat*/
  --panel-background: transparent;
  --panel-background-lighter: transparent;

  --modal-backdrop-solid: transparent;
  --app-background: transparent;
  --startup-background: transparent;


  --audio-progress-metadata: hsl(0deg 0% 0% / 60%);

  --typing: var(--p5-primary);
}


