style.css:
@-moz-document domain('jut.su'){
.top_logo_slogan {
  left:0;
  top:54px;
}
.header.z_fix_header{
  display:none;
}
.logo_b.wrapper {
   margin-bottom: 20px;
  display:inline-flex;
   width: 100%;
  background: linear-gradient(to bottom, #040c0ae3 0%,#00000047 100%);

    max-width: 100%;
      align-items: center;
    justify-content: space-between;
}
.dark_mode .top_logo .top_logo_img {
  height:50px;
  object-fit: none;
  background-size: cover;
}
a.top_logo {
    display: block;
    margin: 0 0 7px 13px; 
    max-width: 181px;
    width: 100%;
}
#up_rank_link2{
  display:none;
}
.logo_b .notice_cont {
  position:relative;
}

/* nav profile */

div.info_panel.clear{
      box-shadow: 3px 4px 0px 0px #394439;
  margin-bottom:7px;
  background: linear-gradient(to bottom, #040c0a 0%,#00000047 100%);
}
.sidebar>.widget, .main{
  background: linear-gradient(to bottom, #040c0a 0%,#00000047 100%);
}
.dark_mode .notice_top2.notice_cont{
   background-color: inherit;
  box-shadow: none;
}
.notice_top2.notice_cont{
      background-color: inherit;
    box-shadow: none;
    border: 3px solid #04301aa8;
    border-radius: 15px;
}
.dark_mode .all_anime_title_halfleft {
    background-color: #2f4f4fad;
    border-radius: 100%;
    visibility: hidden;
}
.aat_ep {
  left:-7px;
}
.header_video {
  border-radius: 13px;
}

.search.s_marg_20 input[type="submit"]{
      background: inherit;
    border: 1px solid #003600ba;
}
.search.s_marg_20  input[type="text"]{
    border: 1px solid #003600ba;
    background: inherit;
    box-shadow: none;
}
.question_use_old_player, .use_old_player, .achiv_switcher_in, .previously_viewed, .widget.kstati_widget, .footer.wrapper.z_fix, .header_video, .posters, .media_content, #wall_editor, .vkbuttw{
   background: linear-gradient(to bottom, #040c0a 0%,#00000047 100%) !important;
}
.use_old_player_info, .dark_mode .notice_additional .notice_cont, .border_around_video{
   border: 1px solid #003600ba;
   background: linear-gradient(to bottom, #040c0a 0%,#000000de 100%);
}
.mCustomScrollBox.mCS-my-scroll.mCSB_vertical.mCSB_inside, .notice_additional {
  max-height: 553px !important;
}
.notice_additional_id{
    display: block;
    border: 2px solid #2c4207;
    overflow: hidden;
    border-radius: 12px;
    right: 5px;
    width: 273px;
}
.header_video{
  border-color:#003600ba !important;
  border-right:2px solid #003600ba !important;
}
 .media_content, .new_last_boruto_eps, .media_content:after{
   border-color:#003600ba !important;
}
.a_dur_line_next span{
      background-color: #023823eb;
}
.anime_next_line_preedge, .anime_next_line_edge{
  background:none !important;
}

#topLoginPanel a:hover {
  text-decoration:none !important;
  padding:0 !important;
  margin:0 !important;
  transition: .2s all ease;
    color: #fff !important;
    background-color: #023823eb !important;
    text-shadow: none !important;
}
#topLoginPanelAvatar{
  background-image:none !important;
  border-radius:5px;
  border:2px solid #023823eb;
    transition: .2s all ease;
}
#topLoginPanelAvatar img{
  margin: 2px 0 0 3px;
}
#topLoginPanelAvatar:hover{
  cursor:pointer;
  border:2px solid #065a3aeb;
}
.us_techs_profile, .video_plate_title > span{
  color:#fff !important;
   background: linear-gradient(to bottom, #040c0a 0%,#000000de 100%) !important;
}

.dark_mode .short-btn.black {
    background: #00443bd9;
    color: #92a372de !important;
}
.dark_mode .short-btn.black:hover {
    background: #015c50d9;
    color: #92a372de !important;
}
.short-btn.green {
    background: #078574d9 !important;
    color: #1f1f1f !important;
}
.short-btn.green:hover {
    background: #38c993d1 !important;
    color: #1f1f1f !important;
}
}
