    /* Logo Adjustment */
        .text-4xl {
            font-size: 50px;
            opacity: 100%;
            position: absolute;
            top: 20%;
            left: 50%;
            transform: translate(-50%, -50%);
        }
    
    /* Add Fake PLUS Logo */
        .text-4xl::after {
            content: "PLUS";
            display: inline-block;
            margin-left: 4px;
            border-radius: 6px;
            background-color: #f7c86c;
            padding-left: 6px;
            padding-right: 6px;
            font-size: 16px;
            font-weight: bold;
            text-transform: uppercase;
            color: #2D3748;
        }
    
