style.css:
@-moz-document domain("allanime.to") {
/*bigger text */
.btn {
	display: inline-block;
	font-weight: 400;
	color: #3e3f3a;
	text-align: center;
	vertical-align: middle;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	background-color: transparent;
	border: 1px solid transparent;
	padding: 0.375rem 0.75rem;
	font-size: 20px;
	line-height: 1.5;
	border-radius: 0.25rem;
	transition: color .15s ease-in-out, background-color .15s ease-in-out, border-color .15s ease-in-out, box-shadow .15s ease-in-out;
}

.card-any-item .card-footer .card-text,
.card-any-item .card-footer .card-text-notes *,
.card-any-item .card-header .card-text,
.card-any-item .card-header .card-text-notes *,
.card-manga-item .card-footer .card-text,
.card-manga-item .card-footer .card-text-notes *,
.card-manga-item .card-header .card-text,
.card-manga-item .card-header .card-text-notes *,
.card-show-item .card-footer .card-text,
.card-show-item .card-footer .card-text-notes *,
.card-show-item .card-header .card-text,
.card-show-item .card-header .card-text-notes * {
	text-align: center;
	width: 100%;
	max-height: 100px;
	overflow: hidden;
	text-overflow: ellipsis;
	font-size: 18px;
	font-family: math;
}

.main-night .breadcrumb,
header.main-night .form-control {
	color: #ddd;
	background-color: #303030;
	font-family: math;
}


.main-night .form-control {
	border: none!important;
	background-color: #303030!important;
	color: #fff!important;
	font-size: large;
	font-family: math;
}

.main-night .dropdown-toggle,
.main-night .text-card {
	color: #be00ff;
	font-family: math;
}

.popular-header {
	background: linear-gradient(90deg, #041136, #000000, #3f10ff);
}

.main-night .dropdown-menu .dropdown-item {
	color: #ddd;
	background: #303030;
	padding-top: 1rem;
	padding-bottom: 1rem;
	font-family: math;
}

.main-night .dropdown-menu .dropdown-item:hover {
	background: #151515;
}

.btn {
	display: inline-block;
	font-weight: 400;
	color: #ffffff;
	text-align: center;
	vertical-align: middle;
	-webkit-user-select: none;
	-moz-user-select: none;
	-ms-user-select: none;
	user-select: none;
	background-color: transparent;
	border: 1px solid transparent;
	padding: 0.375rem 0.75rem;
	font-size: 20px;
	line-height: 1.5;
	border-radius: 0.25rem;
	transition: color .15s ease-in-out, background-color .15s ease-in-out, border-color .15s ease-in-out, box-shadow .15s ease-in-out;
}

.card-any-item .card-footer,
.card-any-item .card-header,
.card-manga-item .card-footer,
.card-manga-item .card-header,
.card-show-item .card-footer,
.card-show-item .card-header {
	color: #fff;
	padding: 0.5rem;
	display: flex;
	flex-direction: column;
	justify-content: center;
	background-color: #3e3f3a;
	font-family: math;
	font-size: large;
}

.card-any-item .card-img-info .card-img-info-left,
.card-any-item .card-img-info .card-img-info-right,
.card-manga-item .card-img-info .card-img-info-left,
.card-manga-item .card-img-info .card-img-info-right,
.card-show-item .card-img-info .card-img-info-left,
.card-show-item .card-img-info .card-img-info-right {
	flex: 0 0 auto;
	white-space: nowrap;
	position: relative;
	color: #fff;
	z-index: 1;
	opacity: 1;
	font-family: math;
	font-size: initial;
}

.sidebar .nav-links li .sub-menu a {
	color: #fff;
	font-size: 20px;
	padding: 15px 1rem;
	white-space: nowrap;
	opacity: .6;
	transition: all .3s ease;
	font-family: math;
}

.main-night .dropdown-toggle,
.main-night .text-card {
	color: #e400ff;
	font-family: math;
}

.main-night .popular-series-title {
	color: #fff;
	font-family: cursive;
}

.tag-headline {
	text-transform: uppercase;
	color: #ddd;
	width: -webkit-min-content;
	width: -moz-min-content;
	width: min-content;
	border-radius: 10px;
	font-family: cursive;
}

.main-night .card-text,
header.main-night .card-body,
header.main-night .card-text {
	color: #ddd;
	font-family: cursive;
	font-size: larger;
}

.main-night .card-body,
.main-night .card-body label {
	color: #ddd;
	font-family: math;
	font-size: medium;
}

.main-night .card-header {
	color: #ddd!important;
	background: rgba(48, 48, 48, .501961)!important;
	border-bottom: none!important;
	font-family: cursive;
	font-size: medium;
}

.main-night .text-secondary {
	color: #c1c1bd!important;
	font-size: medium;
	font-family: cursive;
}
}
