[src="/ui/images/icons/rating_3.svg"],
[src="/ui/images/icons/editors-choice-icon.svg"]
{
	background: url(https://i.ibb.co/MpqLxDN/Thumbs-Up-Skin-Color.png) no-repeat!important;
	box-sizing: border-box;
}

/* Down __________________*/

[src="/ui/images/icons/rating_1.svg"]
{
	background: url(https://i.ibb.co/6XfG8VP/thumbs-dowm.png) no-repeat!important;
	box-sizing: border-box;
}

.like-hover,
.like-icon,
.like-image,

[src="/ui/images/icons/editors-choice-icon.svg"],
[src="/ui/images/icons/rating_1.svg"],
[src="/ui/images/icons/rating_3.svg"]
{
    background-size: 28px!important;
    height: 29px!important;
    padding-left: 29px!important;
    width: 1px!important;
}

/* Trashcan ___________________________________________________________________________________*/

[data-stylish="archive-style"] svg,
[data-stylish="delete-comment"] svg
{
	background: url(https://i.ibb.co/H4Y8YKQ/trash-can-PNG18426.png) no-repeat!important;
	box-sizing: border-box;
}

[data-stylish="archive-style"] svg,
[data-stylish="delete-comment"] svg
{
	background-size: 25px!important;
	height: 26px!important;
	padding-left: 26px!important;
	width: 1px!important;
}

}

