style.css:
@-moz-document domain("minesweeperonline.com") {
/* Site Settings */

* {
	letter-spacing: 1px!important;
	text-decoration: none!important;
	transition: none!important;
}

table {
    border-spacing: 23px 0!important;
}

tr:not(.dialog-bar) td:nth-child(2),
tr:not(.dialog-bar) td:nth-child(3),
tr:not(.dialog-bar) td:nth-child(4) {
    text-align: right!important;
}

/* Remove Items 
_____________________________________________________________________________________________________*/

.left-column, .right-column
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/* Left/Right/Top/Bottom 
_____________________________________________________________________________________________________*/

.scores-time {
    margin-left: 21rem!important;
}

/* Paddings 
_____________________________________________________________________________________________________*/

.scores-pane td,
td[style="padding: 6px;"]
{
	padding: 1.2rem!important;
}

/* Heights 
_____________________________________________________________________________________________________*/

#scores-tabs {
    height: 55px;
}

/* Widths 
_____________________________________________________________________________________________________*/

.scores-name,
.scores-pane
{
	width: auto!important;
}

}

@-moz-document domain("minesweeperonline.com") {
/* Media Query */

@media screen and (min-width: 2560px) {

    
body
{
	font-size: 1.85rem!important;
}

#game {
    transform: scale(2);
}

#game {
    margin-top: 12rem;
    margin-left: 25rem;
}
    
#scores-container {
    position: relative!important;
    top: 10rem!important;
}

#game-container {
    top: 0!important;
}

    #scores-tabs,
#scores-container
{
	width: 100rem!important;
}

.scores-number {
    width: 51px!important;
}

.scores-name {
    width: 349px!important;
}

#footer {
    margin-top: 12rem!important;
}

}

@media (min-width: 1920px) and (max-width: 2559px) {
	
    
body
{
	font-size: 1.5rem!important;
}
    
#game {
    transform: scale(1.5);
}

#game {
    margin-top: 6rem;
    margin-left: 25rem;
}
    
#scores-container {
    position: relative!important;
    top: 5rem!important;
}

#game-container {
    top: 0;
}

    #scores-tabs,
#scores-container
{
	width: 80rem!important;
}

.scores-number {
    width: 51px;
}

.scores-name {
    width: 167px;
}

#footer {
    margin-top: 6rem;
}


}

}

@-moz-document domain("minesweeperonline.com") {
/* Font Settings */

#controls-link,
#display-link,
#export-link,
#import-link,
#options-link,

.scores-tab,

a[href]
{
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited
{
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/* Yellow 
_____________________________________________________________________________________________________*/

#controls-link:hover,
#display-link:hover,
#export-link:hover,
#import-link:hover,
#options-link:hover,

.dialog tr:not(.dialog-bar) td,
.scores-tab:hover,
.scores-tab-selected,
.scores-time,

a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label
 {
	color: #ff0!important;
}

/* White 
_____________________________________________________________________________________________________*/

body,
li,
p
 {
	color: #fff!important;
}

/* Khaki 
_____________________________________________________________________________________________________*/

.dialog-bar,
.dialog-title,
.scores-pane th,

b,
h1,
h2
 {
	color: #F0E890!important;
}

/* Burlywood 
_____________________________________________________________________________________________________*/

h3,
h4,
strong
 {
	color: #e0b880!important;
}

/* Sandy Brown 
_____________________________________________________________________________________________________*/

h5,
h6,
label
 {
	color: #F0A060!important;
}

/*
_____________________________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea
{
	background-color: #5d1b1b!important;
	color: #fff!important;
}

/*
_____________________________________________________________________________________________________*/

::selection
{
	background: #ff9!important;
	color: #000!important;
}

pre ::selection
{
	background: #ff9!important;
	color: #fff!important;
}

::placeholder
{
	color: #f48f8f!important;
	opacity: 1;
}

}

@-moz-document domain("minesweeperonline.com") {
/* Background Settings */

body,
html
{
	background: #000!important;
}

selector
{
	background-image: none!important;
}

/* Background Transparent 
_____________________________________________________________________________________________________*/

.dialog-bar,
.dialog-title
{
	background: #0000!important;
	background-color: #0000!important;
}

/* Plain 
_____________________________________________________________________________________________________*/

.dialog,
.scores-pane td
{
	background-color: #111!important;
	border-radius: 22px!important;
	border: 1px solid rgba(0, 0, 0, .25)!important;
	box-shadow: inset 0 0 5px #0006, #373737 0 1px 0, 10px 10px 5px #0006, #9b9b9b 0 1px 0!important;
	padding: 1.5rem!important;
}

.dialog:hover,
.scores-pane td:hover
{
	box-shadow: inset 0 0 5px #0006, #9b9b9b 0 1px 0, 10px 10px 5px #0a0a0a66, #9b9b9b 0 1px 0!important;
}

/* Highlight Background 
_____________________________________________________________________________________________________*/

span:hover
{
	background: linear-gradient(to right, #3b0202, #0000)!important;
	border-left-color: #434343!important;
}


}

@-moz-document domain("minesweeperonline.com") {
/* Image Settings */

#controls li,

.scores-pane td,
span
 {
	filter: brightness(75%)!important;
}

/*
_____________________________________________________________________________________________________*/

#controls li:hover,

.scores-pane td:hover,
span:hover
 {
	filter: brightness(100%)!important;
}


}

@-moz-document domain("minesweeperonline.com") {
/* New Icons */

input[type="checkbox"],
input[type="radio"] {
    appearance: none!important;
}


/* Lights _______________________________________________________________________________*/

/* On ___________________________*/

.dialog input[type="radio"]:checked
{
	background: url(https://i.ibb.co/njmsz2z/green-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

/* Off ________________________*/

.dialog input[type="radio"]
{
	background: url(https://i.ibb.co/z5C1bGj/red-light-on.png) no-repeat!important;
	box-sizing: border-box;
}

.dialog input[type="radio"]:checked,
.dialog input[type="radio"]
{
    background-size: 35px!important;
    height: 36px!important;
    padding-left: 36px!important;
    width: 1px!important;
}
}

@-moz-document domain("minesweeperonline.com") {
/* Scrollbar Settings */

/* Width 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar {

 	width: 1.2em!important;
 }

 /* Color of handle 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-thumb {
 	background: #580303!important;
 	background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border-radius: 22px!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 ::-webkit-scrollbar-thumb:hover {
 	background: #580303!important;
 	background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of Track 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-track {
 	background: #170000!important;
 	box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
 	border: 1px solid rgba(0, 0, 0, .25)!important;
 }

 /* Color of handles at top and bottom 
_____________________________________________________________________________________________________*/

 ::-webkit-scrollbar-button {
 	background: #4c0707!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

 ::-webkit-scrollbar-button:hover {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

 /* Color of the bottom corner of the scrollable element, where two scrollbar meet. 
_____________________________________________________________________________________________________*/

::-webkit-scrollbar-corner
 {
 	background: #530205!important;
 	box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
 }

::-webkit-scrollbar-corner:hover
 {
 	border-radius: 22px!important;
 	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
 }

}
