style.css:
@-moz-document domain('news.ycombinator.com') {

/* Ocodo's Dark Hacker News v1.0 */ 

body, 
.title, .title a,  
.subtext, 
td, 
.yclinks, 
.pagetop, 
.comment, 
.comhead {
        font-family:sans-serif;
}

/* Global Main Body Background color */
 body {
    background-color: rgb(32, 32, 32);
    color: rgb(230, 230, 230);
}

/* Global Text Area, Input Box and Button color */
 textarea, input {
    background-color: rgb(0, 0, 0);
    color: rgb(255, 255, 255);
    border: 1px solid rgba(255,255,255,0.25);
    border-radius: 10px;
}

button {
  border-radius: 5px;
  border: 1px solid rgba(255,255,255,0.25)
}
/* Content Background color */
 #hnmain {
    background-color: rgb(35, 35, 35);
    color: rgb(230, 230, 230);
}
/* "Y" Logo size */
 #hnmain > tbody > tr:nth-child(1) > td > table > tbody > tr > td:nth-child(1) > a > img {
    width: 2.0em;
    height: 2.0em;
}
/* "Hacker News" Logo/Title Text and main menu positioning */
 .hnname {
    color: rgb(0, 0, 0);
    margin-right: 80px;
    font-size: 1.4em;
}
.hnname a {
    color: rgb(0, 0, 0) !important;
}
.hnname a:link {
    color: rgb(0, 0, 0) !important;
}
.hnname a:hover {
    color: rgb(0, 0, 0) !important;
}
.hnname a:visited {
    color: rgb(0, 0, 0) !important;
}
/* Main Menu text size, seperator colors */
 .pagetop {
    font-size: 1.2em;
    color: rgb(150, 80, 0);
}
/* "Hacker News" Logo/Title text size and color */
 .pagetop b {
    font-weight: bold;
    color: rgb(0, 0, 0) !important;
    margin-left: 5px;
}
.pagetop a {
    color: rgb(0, 0, 0);
}
.pagetop a:link {
    color: rgb(0, 0, 0);
}
.pagetop a:hover {
    color: rgb(255, 255, 255) !important;
}
.pagetop a:visited {
    color: rgb(0, 0, 0);
}
/* Current Page Link Selection */
 .topsel a {
    color: rgb(0, 0, 0);
    font-size: 1.1em;
    font-weight: bold;
}
.topsel a:link {
    color: rgb(0, 0, 0);
}
.topsel a:hover {
    color: rgb(255, 255, 255) !important;
}
.topsel a:visited {
    color: rgb(0, 0, 0);
}
/* Personal Login Name text style and color */
 #me {
}

#hnmain > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) {
    background-color: #f27827;
}

/* Content Link text style and size */
.title a {
    font-size: 1.01em;
}
/* Content Link non-visited color */
 a, .title a {
    color: rgb(180, 180, 180);
}
a:link, .title a:link {
    color: rgb(180, 180, 180);
}
/* Content Link hover color */
 a:hover, .title a:hover {
    color: rgb(255, 255, 255);
}
/* Content Link visited color */
 a:visited, .title a:visited {
    color: rgb(105, 105, 105);
}
/* Content Link Domain emphasis (color) */
 .sitebit a {
    color: rgb(100, 100, 130) !important;
}
.sitebit a:link {
    color: rgb(100, 100, 130) !important;
}
.sitebit a:hover {
    color: rgb(140, 140, 170) !important;
}
.sitebit a:visited {
    color: rgb(90, 90, 110) !important;
}
.sitebit a:visited:hover {
    color: rgb(140, 140, 170) !important;
}
.subtext a {
    color: rgb(130, 130, 130);
}
.subtext a:link {
    color: rgb(130, 130, 130);
}
.subtext a:hover {
    color: rgb(190, 190, 190);
}
.subtext a:visited {
    color: rgb(130, 130, 130);
}
.subtext a:visited:hover {
    color: rgb(190, 190, 190);
}
/* Content Link Subtext position and text size */
 .subtext {
    padding-top: 1px;
    padding-bottom: 3px;
    font-size: 0.65em;
    color: rgb(90, 90, 90);
}
/* Content Score emphasis (color and text bold) */
 .score {
    color: rgb(105, 105, 105);
    font-weight: bold
}
/* Username emphasis (color) */
 .hnuser {
    color: rgb(160, 160, 160) !important;
}
/* Comments Link emphasis (color and text size) */
 .subtext > a:nth-last-child(1) {
    color: rgb(160, 125, 95) !important;
    font-size: 1.1em
}
.subtext > a:nth-last-child(1):link {
    color: rgb(160, 125, 95) !important;
}
.subtext > a:nth-last-child(1):hover {
    color: rgb(210, 160, 120) !important;
}
.subtext > a:nth-last-child(1):visited {
    color: rgb(160, 125, 95) !important;
}
.subtext > a:nth-last-child(1):visited:hover {
    color: rgb(210, 160, 120) !important;
}
/* Comment background color, line height */
 .comment-tree tr.athing, .comment .c00 {
    background: rgb(50, 50, 50);
    color: rgb(210, 210, 210);
    font-size: 1.11em;
    line-height: 1.3em
}
/* Comment content link color */
 .comment .c00 a {
    color: rgb(115, 130, 180);
}
.comment .c00 a:visited {
    color: rgb(110, 120, 130);
}
/* Comment Reply Button color, text style */
 .reply a {
    color: rgb(160, 125, 95) !important;
    font-weight: bold;
    font-size: 1.1em;
    text-decoration: none !important;
}
/* Extra space between table rows (content links, other) */
 tr.spacer {
    height: 9px !important;
}
/* Custom Upvote Arrow */
 .votearrow {
    background: none;
    border-left: rgba(255, 130, 0, .60) 2px solid;
    border-top: rgba(225, 115, 0, .70) 2px solid;
    width: 5px;
    height: 5px;
    margin: 4px 6px;
    padding: 0;
    transform: rotate(45deg);
}
/* Bottom menu link color */
 .yclinks a {
    color: rgb(140, 140, 140);
}
/* Fixes for link visibility on sub pages (user profile page, etc) */
 .profileform a {
    color: rgb(130, 130, 130);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(2) > td:nth-child(2) > a {
    color: rgb(130, 130, 130);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(5) > td:nth-child(2) > a {
    color: rgb(130, 130, 130);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(6) > td:nth-child(2) > a {
    color: rgb(130, 130, 130);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(7) > td:nth-child(2) > a {
    color: rgb(130, 130, 130);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(2) > td:nth-child(2) > a:hover {
    color: rgb(190, 190, 190);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(5) > td:nth-child(2) > a:hover {
    color: rgb(190, 190, 190);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(6) > td:nth-child(2) > a:hover {
    color: rgb(190, 190, 190);
}
#hnmain > tbody > tr:nth-child(3) > td > table > tbody > tr:nth-child(7) > td:nth-child(2) > a:hover {
    color: rgb(190, 190, 190);
}
input[type=submit] {
    color: rgb(255, 150, 0);
    font-size: 1.3em;
    font-weight: bold;
    margin-top: 6px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-left: 8px;
    padding-right: 8px;
}
input[type=text] {
    font-family: Verdana, Geneva, sans-serif;
    padding: 6px;
    margin-bottom: 8px;
}
textarea {
    font-family: Verdana, Geneva, sans-serif;
    padding: 6px;
}}
