style.css:
@-moz-document domain(es.wikipedia.org) {
	body {
  position: relative;
  z-index: 1; /* Asegúrate de que el valor de z-index sea mayor que el del pseudo-elemento */
}

body::before {
  content: "";
  background-image: url('https://i.waifu.pics/n4ooTUm.png');
  background-size: cover;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  z-index: -1;
  filter: blur(10px); /* ajusta el valor según desees */
  background-position: center center;
  background-repeat: no-repeat;
  background-attachment: fixed;
}

.mw-body, .parsoid-body {
    background-color: transparent !important;
}
}
