style.css:
@-moz-document url(https://rumble.com/) {

.channel-subheader--content {
  background: #10212F;
  border:none;
  box-shadow: none;
}

.chat-history--rant-price {
    font-weight: 1;
    font-size: 10px;
}

.chat--height {
  height: 34rem;
}

#chat-sticky-rants {
    max-height: 4rem;
    overflow-y: hidden;
    scroll-behavior: smooth;
    padding: 0rem 1rem 0rem;
    border-top: 1px solid #D6E0EA;
}

div.media-description-with-separator {margin-top: 5px;margin-bottom: 5px;border:none;}

[class="media-content main-content"] {padding-top:18px;}

[class="chat"] {margin-top:-12px;}

.header-search,.header-search-field {height:16px;font-size:12px;}

.header-search-icon {height:12px;margin:3px -20px 0 0;}

.subscribe-buttons button {padding: 0.2rem 1rem;margin: 0px;}

.listing-header--thumb,[class="listing-header--title listing-header--title-with-subscribe-buttons"]
{margin: 0px -40px 22px 50px;}

[class="round-button media-subscribe bg-white"],[class="round-button media-subscribe bg-green"]
{margin: 0px 50px 22px 0px;}

.tuner-box,.bottom-popup--sidebar,.bottom-popup--contents,.bottom-popup{padding:0;}


.media-description--show-button:hover,.media-description--hide-button:hover {background: #172530;}

.bottom-popup{
    background: #061726;
    margin:55px 10px 5px 10px;
    padding:20px;
    border-radius:0.75rem;
}

[class="mediaList-list container content related without-show-more-link"] {
margin-top: -14px;
}

.homepage-view-all 
{float:left;
  border-radius: 5px;
  background:#003500;
  border: 3px solid #008000;
  padding: 2px 5px;}
.homepage-view-all:hover{ 
  background:#008000;
  border: 3px solid #008000;}
.homepage-view-all:active{ 
  background:#61D761;
  border: 3px solid #61D761;}
.homepage-section {
  margin-bottom: 0;
  border-bottom: none;}

.media-description--show-button {
    background: #061726;
    border: #85C742 1px solid;
    color: #FFF;
    border-radius: 10rem;
    float:right;
    margin: 3px 0px 0px 0px;
    font-size:12px;
    padding: 0px 8px 0px 8px;
    height: 24px;
    }

.media-description--hide-button {
    background: #061726;
    border: #85C742 1px solid;
    color: #FFF;
    border-radius: 10rem;
    float:right;
    margin:-40px 0px 0px 0px;
    font-size:10px;
    padding: 8px;
    }

.media-description--expanded {
    background: #061726;
    border: #1f2e3c 3px solid;
    color: #FFF;
    border-radius:10px;
    margin:5px 0px 5px 0px;
    font-size:10px;
    padding: 3px;
    }


[class="container content media-description"]{
    background: #10212F;}
    
.header{
  height: 24px;
}
.header-user-img,.header-user {
  height: 20px;
}

[viewBox="0 0 29.5 30"]{
  width:20px;
  height:20px;
}

.header-search {
  width: 83%;
  margin-left: -15px;
}

.main-menu-nav-collapsed {
  width: 48px;
}

[style="margin: -2rem 0 2rem"] {
  padding: 24px 0 0 0;
  margin: -68px 0 -16px 0;
}

.constrained, a.channel-item, .video-item {
  background: #10212F;
}

.video-item:hover {
  background: #061726;
}

#logo_dark {height: 18px;width: 100px;}

#header-user-menu {width: 178px;}

.homepage-featured {margin-left:-32px;}

#main-menu, .homepage-featured--inner 
{opacity: 0.3;}

#main-menu:hover, .homepage-featured--inner:hover {opacity: 1;}

#main-menu, #header-user-menu 
{margin-top: -46px;}

main {margin-top: 24px;}

.main-menu-signout,.media-by,.homepage-heading-header,.tuner-box--section,.tuner-box--ul 
{margin: 0;}

.listing-header--thumb {
  padding: 24px;
  margin-top: 0;
}

.homepage-featured, .homepage-featured--right, .homepage-featured--left {
  height: 24px;
  margin-top: -28px;
}

#section-editor-picks {margin-top: -32px;}

#video-comments {margin-top: 0px;}

.main-menu-item-channel-label {font-size: 10px;}

.listing-header--letter:before {
  height: 32px;
  width: 32px;
  border: 3px solid #eee;
  line-height: 36px;
  font-size: 32px;
}

.media-by {
  border-bottom: none;
  border-bottom-left-radius: 25px;
  border-bottom-right-radius: 25px;
}

.main-menu-mode-permanent main {margin-left: 32px;}

.listing-header--content-nobacksplash {
  height: 80px;
  padding: 20px 0 0 0;
}

/* Hidden Elements */
.header-upload,
.main-menu-heading-extra,
.locals-button,
.media-engage-share,
.media-video-action-embed,
.chat--rant,
[viewBox="0 0 58 18"],
#section-gaming,
.homepage-featured-gaming,
#section-sports,
.homepage-featured-sports,
.homepage-featured-power-slap,
#section-power-slap,
.homepage-featured-sls,
.homepage-featured-ufc,
.homepage-featured-music,
#section-music,
.homepage-featured-cooking,
#section-cooking,
.homepage-featured-finance,
#section-finance,
.video-category-tag.video-category-tag-tag,
.media-locals-message.media-locals-message--with-separator,.video-category-tag-list,.media-locals-message,#chat--emoji-toggle-button {
  display: none;
}

/* Miscellaneous */
.main-menu-item-label-collapsed {
  font-size: 8px;
}

h1.h1 {
  font-size: 16px;
  padding: 2px 0 4px 0;
}

.main-menu-heading {
  border: none;
}


.video-listing-entry,h1,.streamed-on,.video-footer-buttons,.media-description-section {
  margin: 0;
}

.main-menu-item, .main-menu-item-channel, .main-menu-heading, .homepage-featured--inner, .homepage-item--live, .homepage-feature {
  padding: 0;
}

div.media-description {
  padding: 0 10px 0 10px;
}

#logo_light {
  margin-left:-12px;
  height:18px;
  width: 120px;
}

#msg {font-size: 8px;}

}
