style.css:
@-moz-document domain("atlassian.net") {
@font-face {
font-family: Vazir;
src: url('https://cdn.jsdelivr.net/gh/rastikerdar/vazir-font@v26.0.2/dist/Vazir.ttf') format('truetype');
}

@font-face {
font-family: Vazir Code;
src: url('https://cdn.rawgit.com/rastikerdar/vazir-code-font/v1.1.2/dist/Vazir-Code.ttf') format('truetype');
}

#title-text, .wiki-content {
font-family: Vazir;
}

.wiki-content {
text-align: justify;
text-indent: 1em;
}

code {
font-family: "quotVazir Code";
background: rgba(0,0,0,0.03);
border: 1px solid #dfe1e5;
border-radius: 3px;
padding: 1px 6px;
}

div.ak-editor-content-area {
direction: rtl;
line-height: 2rem;
}

div.ak-editor-content-area .media-extended-resize-experience {
direction: ltr !important;
}


div#inline-comment-sidebar {
direction: rtl;
}

div.ReactVirtualized__Grid__innerScrollContainer {
direction: rtl;
}

h1[data-test-id="issue.views.issue-base.foundation.summary.heading"] {
direction: rtl;
}

div[data-test-id="issue.views.field.rich-text.description"] {
direction: rtl;
}

div.ak-renderer-document p{
direction: rtl;
line-height: 2rem;
} 
div.ak-renderer-documen ul {
direction: rtl;
line-height: 2rem;
}
div.ak-renderer-documen ol {
direction: rtl;
line-height: 2rem;
} 
div.ak-renderer-documen h1 {
direction: rtl;
line-height: 2rem;
} 
div.ak-renderer-documen h2 {
direction: rtl;
line-height: 2rem;
} 
div.ak-renderer-documen h3{
direction: rtl;
line-height: 2rem;
}
div.ak-renderer-documen h4{
direction: rtl;
line-height: 2rem;
}
div.ak-renderer-documen h5{
direction: rtl;
line-height: 2rem;
}
div.ak-renderer-documen h6 {
direction: rtl;
line-height: 2rem;
} 

ul li ul li {
margin: 0 10px;
}
}
