style.css:
@-moz-document domain(cybhack.net),
domain("cybhack.net") {
  
body {
    background-image: url("/*[[cybhack-background]]*/");
	background-size: 100%;
	background-attachment: fixed;
        color: rgb(214, 214, 214);
        font: 13 px /1.231 arial, helvetica, clean, sans-serif;
        *font-size: small;
        *font: x-small;
width: 100%;
    height: 100%;
}

.block-container{
  opacity: .95;
}

.p-body-header{
  display: none;
}

.button.button--cta, button.button a.button.button--cta {
    color: #fff;
    background: #152ac7;
    border: none;
    border-color: #152ac7;
		box-shadow: 0px 0px 25px 0px rgba(21, 42, 199, 1);
  border-radius: 9px 9px 9px 9px;
}

.button.button--cta:hover, button.button a.button.button--cta:hover, .button.button--cta:focus, button.button a.button.button--cta:focus {
    background: #152ac7;
  border-radius: 9px 9px 9px 9px;
}

.button, button.button a.button {
  color: #fff;
  background: #152ac7;
    border-color: #152ac7;
  box-shadow: 0px 0px 25px 0px rgba(21, 42, 199, 1);
  border-radius: 9px 9px 9px 9px;
}

.button:hover, button.button a.button:hover, .button:focus, button.button a.button:focus {
    background: #152ac7;
}

.button.button--link:hover, button.button a.button.button--link:hover, .button.button--link:focus, button.button a.button.button--link:focus {
    text-decoration: none;
    background: #152ac7;
}

.button.button--link, button.button a.button.button--link {
    font-size: 1.3rem;
    padding: 0;
    padding-right: 8px;
    padding-left: 8px;
    height: 28px;
    line-height: 28px;
    background: #152ac7;
    border: 1px solid #152ac7;
    text-transform: initial;
}

.button.button--primary, button.button a.button.button--primary {
    color: #fff;
    background: #152ac7;
    border: none;
    border-color: #152ac7;
}

.tc-nlw-widget .tc-nlw-widget--body .nodeList .nodeList-item .nodeList-itemRow:not(.nodeList-itemRow--category) {
    color: #ffffff;
    text-decoration: none;
    border-radius: 7px;
  text-shadow: 0px 0px 0px rgba(255, 255, 255, 1);
}

.button.button--primary:hover, button.button a.button.button--primary:hover, .button.button--primary:focus, button.button a.button.button--primary:focus {
    color: #fff;
    background: #152ac7;
}

.is-sticky .p-nav-inner .p-header-logo.p-header-logo--image img {
    filter: hue-rotate(214deg);
}

.p-nav-opposite {
    border-left: 2px solid #152ac7;
}

::-webkit-scrollbar-thumb {
    background: #152ac7;
    border-radius: 7px;
}

.block[data-widget-key="forum_overview_forum_statistics"] .stats-progress .progress {
    background: #152ac7;
    height: 20px;
  box-shadow: 0px 0px 25px 0px rgba(21, 42, 199, 1);
}
.menu-linkRow.is-selected, .menu-linkRow:hover, .menu-linkRow:focus {
    color: #fff;
    text-decoration: none;
    background: #1f242e;
}

.blockLink.is-selected {
    color: #fff;
    font-weight: 600;
    background: #29303d;
    border-left: 2px solid #152ac7;
    padding-left: 13px;
}

.breadcrumb.block {
  border-radius: 9px 9px 9px 9px;
}

.buttons-bottom-avatar .button:not(.button--dots) {
    background: #152ac7;
}

.buttons-bottom-avatar .button--dots {
    background: #152ac7;
}

.block-tabHeader .tabs-tab.is-active {
    color: #fff;
    border-color: #b44141;
   border-radius: 9px;
}

.block-tabHeader .tabs-tab.is-active {
    color: #fff;
    border-color: #152ac7;
    border-radius: 9px;
}

.block-tabHeader .tabs-tab {
    padding: 15px 15px;
    border-bottom: 2px solid #152ac7;
}

.block-tabHeader .tabs-tab:hover {
    color: #fff;
    background: 0;
}

.message-avatar-wrapper:before {
    border-left: 1px solid #152ac7;
    border-right: 1px solid #152ac7;
}

.pageNav-page.pageNav-page--current {
    color: #fff;
    box-shadow: 0 -2px #152ac7 inset;
    cursor: pointer;
}

.label.label--admin, .userBanner.userBanner--admin {
    color: #fff;
    background: #152ac7;
}

.lfs ::-webkit-scrollbar-thumb:hover, .real-time-chat ::-webkit-scrollbar-thumb:hover, .menu-content ::-webkit-scrollbar-thumb:hover {
    background: #fff;
}

.lfs ::-webkit-scrollbar-thumb, .real-time-chat ::-webkit-scrollbar-thumb, .menu-content ::-webkit-scrollbar-thumb {
    background: #152ac7;
    border: 0px none #fff;
}

.dataList-row.dataList-row--subSection .dataList-cell {
    font-size: 1.8rem;
    border-bottom: 1px solid #232934;
    padding: 7.5px 15px;
    background: rgb(62 64 241);
    border-radius: 8px;
}
 .bbCodeBlock {
    border-left: 2px solid #2322b3;
}
.tc-fb-badge.tc-fb-userGroupBadge--staff {
box-shadow: 0px 0px 15px 0px rgba(150, 68, 72, 1);
}

.pairs>dt {
    color: rgba(89, 131, 252, 1);
    text-shadow: 0px 0px 9px rgba(82, 97, 231, 1);

}
.structItemContainer .structItemContainer-group--sticky .structItem--thread {
    border-left: 3px solid #152ac7;
}

.structItem-status--sticky::before {
    color: #152ac7;
}

.label.label--green, .userBanner.userBanner--green {
  box-shadow: 0px 0px 17px 0px rgba(29, 83, 59, 1);
}
.label.label--important {
  box-shadow: 0px 0px 17px 0px rgba(136, 68, 68, 1)
}
.label.label--contest {
  box-shadow: 0px 0px 17px 0px rgba(124, 77, 174, 1);
}
.input:focus, .input.is-focused, .inputGroup:focus-within, #XF .fr-box.fr-basic.is-focused, .codeEditor.CodeMirror.CodeMirror-focused {
    --input-border-heavy: #7C4DAE;
    --input-border-light: #7C4DAE;
}

.block-tabHeader {
    color: #cbcbcb;
    background: #7d7c7c30;
    border-bottom: 1px solid #20468b;
    font-weight: 700;
    font-size: 1;
}

.notice.notice--primary {
    border: 2px solid #5039ba;
box-shadow: 0px 0px 24px -7px rgba(80, 57, 186, 1);
}

.notice.notice--primary .uix_noticeIcon {
    background: #5039ba;
}

.message-cell.message-cell--alert {
    font-size: 1.4rem;
    flex: 1 1 auto;
    width: 100%;
    min-width: 0;
    color: #ffffff;
    background: #20468b;
}

.message-cell.message-cell--alert a {
    color: #A290F4;
}

.button.button--scroll, button.button a.button.button--scroll {
    background: #20468b;
    box-shadow: 0px 0px 24px -7px rgba(80, 57, 186, 1);
}

.button.button--scroll:hover, button.button a.button.button--scroll:hover, .button.button--scroll:focus, button.button a.button.button--scroll:focus {
    color: #fff;
    background: #20468B;
box-shadow: 0px 0px 24px -7px rgba(80, 57, 186, 1);
}
}
