style.css:
@-moz-document domain('hurawatch.cc') {
  
/* Click Activate Breakpoints from the sources page to enable CSS editing */

/* homepage */

#mw-home .mwh-logo-div{
  background: url(https://cdn.iconscout.com/icon/free/png-256/free-netflix-282224.png?w=250&h=80&scale.option=fit) no-repeat;
    width: 250px;
    height: 80px;
}

#mw-home .mwh-logo-div img{
   visibility: hidden;
}

.description{
  visibility: hidden;
  display: none;
  position: absolute;
}


#main-wrapper > .container > #mw-home{
  display: flex;
  justify-content: center;
  align-items: center;
  height: 85vh;
}

.home_page{
  height: 100vh
}

.mw-heading{
  display: none;
}

#mw-home .btn-home{
  background-color: #B71C23 !important;
  border: none !important;
  
}

/* Universal */

a{
  color: #fff !important
}

body{
  background-color: #050505;
}

::-webkit-scrollbar {
  width: 10px;
  height: 7px;
}

::-webkit-scrollbar-track {
  background: none;
}

::-webkit-scrollbar-thumb {
  background: #555;
  border-radius: 10px
}

/* Ad blocking*/

html > iframe:nth-child(7){
  display: none;
}

#st-1{
  display: none;
}

html > iframe:nth-child(5){
   display: none;
  visibility: hidden;
}

/* Header */

#logo{
  background: url(https://cdn.iconscout.com/icon/free/png-256/free-netflix-282224.png?w=130&h=35&scale.option=fit) no-repeat;
    width: 140px;
    height: 40px;
}

.fa-list:before{
  content: "\f0c9";
  color: #fff;
}

#sidebar_menu > ul > li:nth-child(7){
  visibility: hidden;
  display: none;
}

.nav{
  margin-bottom: -50px;
}

#header{
  background-color: #0C0C0C;
}

#header #logo img{
  visibility: hidden;
}
#header #logo{
  margin-bottom: 20px;
}

#user-slot > div > a{
  display: none;
  visibility: hidden;
}

#header_menu > ul > li:nth-child(7){
  display: none;
  visibility: hidden;
}

@media screen and (min-width: 760px){
#search{
  float: right;
  width:450px;
}
}

@media screen and (max-width: 759px){
#search{
  display: none;
  visibility: hidden;
}
}

@media screen and (max-width: 1279px){
  #search{
    width: 250px;
  }
}


.search-content{
  width: 400px !important;
}

.search-content input.search-input{
  height: 44px;
    color: #fff;
    padding-right: 20px;
    padding-left: 55px;
    font-size: 15px;
    font-weight: 400;
    background: rgba(255,255,255,.1);
    border-radius: 30px;
    border: none;
    box-shadow: none!important;
}

.search-content .search-result-pop .nav-bottom{
  background-color: #303030 !important;
  color: #fff;
}

.search-content .search-result-pop{
  background-color: #0c0c0c;
}

.search-content .search-result-pop .nav-item:nth-of-type(2n){
  background-color: #0f0f0f;
}

.search-content .search-result-pop .nav-item:hover .srp-detail .film-name{
  color: #fff;
}

.search-content .search-result-pop .nav-item:hover{
  background: #590a0e;
 transition: background-color 0.2s;
}

.search-content .search-result-pop .nav-item{
   transition: background-color 0.2s;
}

.search-content .search-result-pop .nav-item .srp-detail{
  color: #666;
}

.search-content .search-result-pop .nav-item:hover .srp-detail{
  color: #fff;
}

.search-content .search-result-pop{
  top: 75px;
}


/* Footer*/

#footer .top{
  display: none;
  visibility: hidden;
}

/* Main Page */

@media screen and (min-width: 1660px) {#header.header-home{
  margin-bottom: -15vh !important;
}
}



@media screen and (min-width: 1660px){ #trending-movies{
  
  margin-top: -20vh !important;
}
}



	/* > Slider < */

#slider{
  height: 80vh;
}

#slider > div.swiper-wrapper{
  transition-delay: 120s;
}

#slider .slide-caption .sc-detail{
  display: none;
}

#slider .slide-caption p.sc-desc{
  font-size: 16px;
}

#slider > div.container.container-pagination > div{
  display: none;
}

#slider .swiper-slide:before{
  background: linear-gradient(to bottom, transparent, #050505);
  height: 50vh
}

@media screen and (min-width: 1301px) {
#slider .slide-caption {
  bottom: 30vh;
  width: 35%
}
}

@media screen and (max-width: 1301px){
  #slider .slide-caption {
  bottom: 20vh;
  width: 35%
}
}

#slider .slide-caption .film-title a{
  text-transform: uppercase;
  font-size: 56px;
  font-weight: bold;
  line-height: 56px
}


#slider .slide-caption .sc-buttons .btn-sc-action::after{
  content: "Play Now";
  font-size: 14px;
  color: #FFE5DD
}

.fa-play:before{
color: #FFE5DD
}
#slider .slide-caption .sc-buttons .btn-sc-action{
  font-size: 0px;
}

#slider .slide-caption .sc-buttons .btn-sc-action.btn-sc-play{
  padding: 0px 30px;
  border-radius: 5px;
  border-color: #B71C23;
  background-color: #B71C23;
}


	/* > Film List < */

.film_list .film_list-wrap{
  padding: 30px;
  margin: -30px
}

.film_list-wrap{
  overflow-x: scroll;
  display: flex;
  margin-bottom: 50px !important;
}



/* TO DO: Fix Positioning and interactiveness of slider buttons */

#block_area-content #block_area-list #film_list #film_list-grid::before{
  postion: relative;
  align-self: center;
  content: "⮜";
  display: flex;
  justify-content: center; 
  align-items: center;
  font-size: 21px;
  width: 40px;
  height: 20px;
  margin-right: -20px;
  background-color: white;
  z-index: 8;
  padding: 20px;
  border-radius: 20px;
}

#block_area-content #block_area-list #film_list #film_list-grid::after{
  postion: relative;
  align-self: center;
  content: "⮞";
  display: flex;
  justify-content: center; 
  align-items: center;
  font-size: 21px;
  width: 40px;
  height: 20px;
  margin-left: -20px;
  background-color: white;
  z-index: 8;
  padding: 20px;
  border-radius: 20px;
}


.flw-item{
  flex: 0 0 auto;
  margin: 0 .4% 1% !important;
  z-index: 5;
  transition: transform 0.3s ease;
}

#main-wrapper > div > section:nth-child(6) > div.block_area-content.block_area-list.film_list.film_list-grid > div{
  flex-wrap: inherit !important;
}

#main-wrapper > div > section:nth-child(7) > div.block_area-content.block_area-list.film_list.film_list-grid > div{
  flex-wrap: inherit !important;
}

#main-wrapper > div > section:nth-child(8) > div.block_area-content.block_area-list.film_list.film_list-grid > div{
  flex-wrap: inherit !important;
}


#main-wrapper > div > section.block_area.block_area_home.section-id-01 > div.block_area-header.block_area-header-tabs{
  visibility: hidden;
  display: none;
}

.film-poster .film-poster-img{
  z-index: 4 !important;
  background-color: #222;
  color: #fffff;
  font-size: 16px;
}

.flw-item:hover{
  transform: scale(1.2);
  transition: transform 0.3s ease;
  z-index: 6;
  width: 10vw;
  height: 100%;
  margin-left: 100px;
  margin-right: 100px
}


.flw-item .film-poster .film-poster-ahref:before{
  display: none;
}
.flw-item .film-poster .film-poster-ahref i{
  display: none;
}

.flw-item .film-poster .film-poster-ahref{
  z-index: 5;
}

#trending-movies > div > div > div > div.film-detail{
  visibility: hidden;
  display: none;
}

.film_list .film_list-wrap .flw-item .film-detail{
  display: none;
}

/* Search Grid*/
#main-wrapper > div > section > div.block_area-content.block_area-list.film_list.film_list-grid > div{
  flex-wrap: wrap;
}

/* Video Player */
#main-wrapper > div.movie_information > div{
  flex-wrap: wrap;
  display: table;
  margin-top: 50px;
}

#comment > section{
  display: none;
}

#main-wrapper > div.movie_information > div > div.m_i-detail > div.m_i-d-content > div.elements > div:nth-child(6){
  display: none;
}

#block-rating{
  display: none;
}

.movie_information .m_i-related{
  width: 100%;
  display: none; /* TEMP */
}

@media screen and (min-width: 1301px){
  .film-related .film_list .film_list-wrap .flw-item{
    width: 11.7%;
  }
}

#main-wrapper > div.control_watch_block > div:nth-child(1) > div{
  display: none;
}

.page-detail{
  display: table;
  margin: 0 auto;
}

.control_watch_block{
  display: table-footer-group;
}

#main-wrapper > div.watch_block.detail_page-watch{
  width: 100vw;
}


.w_b-cover:after{
  background: linear-gradient(0deg,#050505 5%,rgba(1,2,4,0) 100%);
}


.movie_information{
  display: table-row-group;
}

#content-episodes{
  padding-top: 30px;
  padding-bottom: 40px;
}

.seasons-list{
  padding-bottom: 100px;
}

.seasons-list-new .sl-content .slc-eps .slce-list li{
  width: calc(30% - 10px)
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link{
  display: grid;
  align-items: top;
  border-radius: 10px;
  padding-top: 20px;
  padding-bottom: 20px;
  background-color: #141414 !important;
  border-color: #141414 !important;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link i{
  display: none;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link strong{
  display: grid;
  font-size: 12px;
  margin-bottom: 5px;
  color: #666 !important;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link {
  font-size: 16px;
  font-family: Arial;
  color: #999 !important;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link:hover{
  background-color: #333333 !important;
  border-color: #333333 !important;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link.active{
  background-color: #6A1114 !important;
  color: #fff !important;
    border-color: #050505 !important;
}

.seasons-list .sl-content .slc-eps .slce-list .nav-link.active strong{
  color: #fff !important;
}

.dropdown-menu-new{
  background-color: #222222;
}

.server-select .nav .nav-item .nav-link.active{
	color: #fff!important;
    border-color: #fff!important;
}

.server-select .nav .nav-item .nav-link{
	background: #141414 !important;
	border: 1px solid #141414 !important;
}

.server-select .nav .nav-item .nav-link:hover{
  background: #333333 !important;
	border: 1px solid #101010 !important;
}

.server-select{
	padding: 30px 0 90px;
}


/* Colour Swapping */

.wbp-play:hover{
  background-color: #B71C23;
}

.flw-item .film-poster .film-poster-ahref:before{
  background-color: #E34F56;
}

.m_i-d-content .stats .btn-quality{
  background-color: #B71C23;
  color: #FFE5DD;
}

.pre-pagination .pagination .page-item.active .page-link{
  background-color: #B71C23;
  color: #FFE5DD;
}

.pre-pagination .pagination .page-item .page-link{
  background: #141414;
}

.bah-filter .btn{
  background: #141414 !important;
}

.btn-focus{
   background-color: #B71C23 !important;
  color: #FFE5DD !important;
}

.btn-secondary{
  background: #141414 !important;
  color: #fff !important;
}

.btn-secondary:hover{
  background: #333333 !important;
  color: #fff !important;
}

.pre-pagination .pagination .page-item .page-link:hover{
  background: #333333;
}

.dropdown-menu-new .dropdown-item.active{
  background-color: #B71C23 !important;
  color: #FFE5DD;
}

.pre-tabs .nav-item .nav-link.active{
  background-color: #B71C23;
  color: #FFE5DD;
}

.pre-tabs .nav-item .nav-link:hover{
  background-color: #B71C23;
  opacity: 70%;
   color: #FFE5DD;
}
}
