style.css:
body {
    --custom-accent-hue: /*[[custom-accent-hue]]*/;
    --color-calendar-graph-day-L1-bg: hsl(var(--custom-accent-hue), 61%, 17%);
    --color-calendar-graph-day-L2-bg: hsl(var(--custom-accent-hue), 100%, 21%);
    --color-calendar-graph-day-L3-bg: hsl(var(--custom-accent-hue), 63%, 42%);
    --color-calendar-graph-day-L4-bg: hsl(var(--custom-accent-hue), 77%, 59%);
    --color-social-reaction-bg-reacted-hover: hsl(var(--custom-accent-hue), 73%, 28%, 33%);
    --button-invisible-fgColor-rest: var(--fgColor-link);
    --button-primary-bgColor-rest: var(--bgColor-btn-primary);
    --button-primary-bgColor-hover: var(--bgColor-btn-primary-hover);
    --borderColor-accent-emphasis: hsl(var(--custom-accent-hue), 84%, 52%);
    --borderColor-accent-subtle: hsla(var(--custom-accent-hue), 98%, 61%, 0.1);
    --borderColor-accent-muted: hsla(var(--custom-accent-hue), 98%, 61%, 0.4);
    --borderColor-open-emphasis: hsl(calc(var(--custom-accent-hue) + 2), 59%, 33%);
    --borderColor-success-emphasis: hsl(calc(var(--custom-accent-hue) + 2), 59%, 33%);
    --bgColor-accent-muted: hsl(var(--custom-accent-hue), 70%, 21%, 12%);
    --bgColor-accent-emphasis: hsl(var(--custom-accent-hue), 87%, 23%);
    --bgColor-btn-primary: hsl(var(--custom-accent-hue), 59%, 33%);
    --bgColor-btn-primary-hover: hsl(var(--custom-accent-hue), 55%, 40%);
    --bgColor-btn-primary-selected: hsl(var(--custom-accent-hue), 59%, 33%);
    --bgColor-btn-primary-disabled: hsla(var(--custom-accent-hue), 59%, 33%, 0.6);
    --bgColor-danger-emphasis: hsl(calc(var(--custom-accent-hue) + 10), 45%, 22%);
    --bgColor-success-emphasis: hsl(calc(var(--custom-accent-hue) - 10), 65%, 45%);
    --fgColor-link: hsl(var(--custom-accent-hue), 93%, 58%);
    --fgColor-open: hsl(var(--custom-accent-hue), 49%, 49%);
    --fgColor-accent: hsl(var(--custom-accent-hue), 93%, 58%);
    --fgColor-success: hsl(var(--custom-accent-hue), 49%, 49%);
    --diffBlob-addition-bgColor-num: hsl(var(--custom-accent-hue), 20%, 9%);
    --diffBlob-addition-bgColor-line: hsl(calc(var(--custom-accent-hue) - 10), 29%, 19%);
    --diffBlob-deletion-bgColor-line: hsl(calc(var(--custom-accent-hue) + 10), 22%, 15%);
    --focus-outlineColor: hsl(var(--custom-accent-hue), 37%, 45%);
}

input[type="checkbox"] {
  accent-color: var(--bgColor-btn-primary);
}
