style.css:
@-moz-document url-prefix("https://v2.horsereality.com/")
{
  :root {
    --light-sage: #55559ca6;
    --picture-button: #15183ab5;
    --gold: #00000000;
    --light-green: #55559c;
    --gold-text: #ffffff69;
    --light-text: #ffffff;
    --sand: #55559c;
    --body: #2e316a5c;
    --green-text: #ffffff69;
    --gold-hover: #a9a9a9;
    --green-hover: #40448d96;
    --progress: #2e316a;
    --button: #55559c;
    --button-hover: #474783;
    --boxes: #55559c;
    --white: #ffffff;
    --odd: #40448d96;
    --even: #1e20464f;
    --mine: #2e316a;
    --select: #2e316a;
    --top-hover: #2e316aa6;
    --background: url(https://cdn.discordapp.com/attachments/995042732781350982/1065282763449708584/New_Project_29.png);
    --map: url(https://cdn.discordapp.com/attachments/995042732781350982/1065284200376324146/New_Project_89_5_1_2_1_2_2_1_1_6_3_2_4_2_2_1_1_1_1.png);
    --title-font: Cormorant SC;
    --main-font: Cormorant SC;
    --headers: url(https://cdn.discordapp.com/attachments/995042732781350982/1065668945689980968/New_Project_30.png);
  }

  .component.table .table {
    background-color: #fff0;
    margin: 0;
  }
}

@-moz-document url-prefix("https://www.horsereality.com/")
{
  :root {
    --light-sage: #55559ca6;
    --picture-button: #15183ab5;
    --gold: #00000000;
    --light-green: #55559c;
    --gold-text: #ffffff69;
    --light-text: #ffffff;
    --sand: #55559c;
    --body: #2e316a5c;
    --green-text: #ffffff69;
    --gold-hover: #a9a9a9;
    --green-hover: #40448d96;
    --progress: #2e316a;
    --button: #55559c;
    --button-hover: #474783;
    --boxes: #55559c;
    --white: #ffffff;
    --odd: #40448d96;
    --even: #1e20464f;
    --mine: #2e316a;
    --select: #2e316a;
    --top-hover: #2e316aa6;
    --background: url(https://cdn.discordapp.com/attachments/995042732781350982/1065282763449708584/New_Project_29.png);
    --horses: url(https://www.horsereality.com/images/profilebg_seaofflowers.jpg);
    --map: url(https://cdn.discordapp.com/attachments/995042732781350982/1065284200376324146/New_Project_89_5_1_2_1_2_2_1_1_6_3_2_4_2_2_1_1_1_1.png);
    --title-font: Cormorant SC;
    --main-font: Cormorant SC;
  }
}

@-moz-document url-prefix("https://v2.horsereality.com/delta-store")
{
  .delta-store-item-content {
    position: relative;
    padding-top: 30px;
    padding-bottom: 30px;
    border-top-left-radius: 8px;
    border-top-right-radius: 8px;
    background: var(--even);
  }

  .button.primary {
    background: var(--button);
  }
  .button.primary:hover {
    background: var(--button-hover);
  }

  .delta-store-item-footer {
    display: -webkit-box;
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    -webkit-box-pack: justify;
    justify-content: space-between;
    padding: 10px;
    color: #fff;
    border-bottom-right-radius: 8px;
    border-bottom-left-radius: 8px;
    background: var(--odd);
  }

  .delta-store-bonus {
    font-size: 10px;
    font-weight: 700;
    line-height: 16px;
    position: absolute;
    z-index: 100;
    top: 3px;
    right: -3px;
    padding: 2px 5px;
    -webkit-transform: rotate(15deg);
    transform: rotate(15deg);
    color: #fff;
    border-radius: 3px;
    background: var(--button);
  }

  .delta-store-tag {
    font-size: 11px;
    font-weight: 700;
    padding: 2px 5px;
    text-transform: uppercase;
    color: #fff;
    border-radius: 3px;
    background: var(--button);
  }

  .delta-store-item-price-total {
    font-size: 24px;
    font-weight: 700;
    line-height: 1;
    color: #fff;
  }

  .notice.warning {
    color: #ffffff;
    border: 1px solid #ffeeba00;
    background: #55559c80;
  }

  .button.black,
  .button.secondary.active,
  .button.secondary:hover {
    color: #fff;
    background: var(--button);
  }

  .delta-store-service-wrapper {
    display: -webkit-box;
    display: flex;
    -webkit-box-align: start;
    align-items: flex-start;
    -webkit-box-pack: center;
    justify-content: center;
    width: calc(100% + 30px);
    margin-bottom: -15px;
    margin-left: -15px;
    padding: 30px 38px;
    background: var(--even);
  }

  .delta-store-service header {
    position: relative;
    padding: 12px 15px 9px;
    background: #55559c7a;
  }

  .delta-store-service header h3 .delta-store-tag.active {
    background: var(--button);
  }

  .delta-store-service footer {
    padding: 15px;
    border-top: 1px solid #0000;
  }

  .delta-store-support {
    margin-top: 15px;
    margin-bottom: -15px;
    opacity: 0%;
  }

  .delta-store-best-deal-yellow {
    bottom: -40px;
    opacity: 0%;
  }

  img[src="https://www.horsereality.com/images/dp-horse.png"]
  {
    content: url(https://cdn.discordapp.com/attachments/995042732781350982/1065676798039441519/dp-horse.png);
  }

  .delta-store-best-deal {
    height: 200px;
    border-radius: 6px;
    background: var(--even);
  }

  .delta-store-item-icon:before {
    position: absolute;
    top: -10px;
    display: block;
    width: 80px;
    height: 80px;
    content: "";
    border-radius: 50%;
    background: #ffffff1f;
  }

  .delta-store-service {
    overflow: hidden;
    width: 100%;
    max-width: 300px;
    border-radius: 8px;
    background: var(--even);
  }
}

@-moz-document url-prefix("https://v2.horsereality.com/foal-calendar")
{
  .button.primary {
    background: var(--button);
  }
  .button.primary:hover {
    background: var(--button-hover);
  }
  .tabs-wrapper:after {
    position: absolute;
    z-index: 1;
    right: -15px;
    bottom: 0;
    left: -15px;
    height: 1px;
    content: "";
    background: #30305c;
  }
  .info .info-icon {
    font-size: 11px;
    display: -webkit-box;
    display: flex;
    -webkit-box-align: center;
    align-items: center;
    flex-shrink: 0;
    -webkit-box-pack: center;
    justify-content: center;
    width: 30px;
    height: 100%;
    margin-right: 8px;
    color: #000000;
    background: #30305c7a;
  }
  .info {
    font-weight: 700;
    display: -webkit-box;
    display: flex;
    overflow: hidden;
    -webkit-box-align: center;
    align-items: center;
    height: 20px;
    padding-right: 8px;
    border-radius: 4px;
    background: #30305c7a;
    box-shadow: inset 0 0 0 1px #00000073;
  }
  img[src="https://www.horsereality.com/icon/sex-mare.png"]
  {
    content: url(https://cdn.discordapp.com/attachments/993766176914153482/1032338320073699438/Sex_-_Mare_Small.png);
  }
  img[src="https://www.horsereality.com/icon/sex-stallion.png"]
  {
    content: url(https://cdn.discordapp.com/attachments/993766176914153482/1032338320509902868/Sex_-_Stallion_Small.png);
  }
  .fa-filter:before {
    content: "\F0B0";
    color: #000;
  }
  .foal-calendar-table .filter {
    font-weight: 700;
    cursor: pointer;
    color: #000 !important;
  }
  .modal-inner {
    width: 80%;
    max-width: 400px;
    padding: 10px;
    -webkit-transform: scale(0.9);
    transform: scale(0.9);
    border-radius: 8px;
    background: #30305c78;
    box-shadow: 0 3px 6px rgb(0 0 0 / 16%);
  }
  .tabs .tab.active {
    background: var(--odd);
  }
  .tabs .tab {
    position: relative;
    z-index: 2;
    margin-right: 3px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    border-radius: 8px 8px 0 0;
    background: var(--even);
  }
  .tabs .tab > span,
  .tabs .tab a,
  .tabs .tab input {
    font-size: 13px;
    font-weight: 700;
    line-height: 20px;
    color: #000;
  }
  .tabs .tab.active a {
    color: #000;
  }
}

