@-moz-document url-prefix("https://v2.horsereality.com/horses")
{
  .horse-list.alternate .horse-item:nth-child(2n) .content {
    background: var(--odd);
  }

  .horse-item .content {
    overflow: hidden;
    border-radius: 8px;
    background: var(--even);
    border-radius: 50px 50px 50px 50px;
  }
  .button.primary {
    background: var(--button);
  }
  .button {
    font-size: 12px;
    font-weight: 700;
    line-height: 18px;
    min-width: 100px;
    padding: 4px 16px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    text-align: center;
    color: var(--light-text);
    border: 0;
    border-radius: 30px;
  }
  .stall-tabs .stall-tab > span,
  .stall-tabs .stall-tab a {
    font-size: 13px;
    font-weight: 700;
    line-height: 20px;
    display: block;
    padding: 6px 16px;
    white-space: nowrap;
    color: var(--dark-text);
  }
  .stall-tabs .stall-tab {
    position: relative;
    z-index: 2;
    margin-right: 3px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    border-radius: 8px 8px 0 0;
    background: var(--select);
  }
  .button.primary:hover {
    background: var(--button-hover);
  }
  .horse-filters-search input {
    width: 100%;
    height: 32px;
    padding: 3px 10px 3px 30px;
    border: 0;
    border-radius: 8px;
    background: var(--odd);
  }
  .current-capacity:hover {
    background: var(--button-hover);
  }
  .stall-tabs .stall-tab.active a {
    color: var(--dark-text);
    font-family: var(--title-font);
  }
  .stall-tabs .stall-tab.active {
    background: var(--even);
    font-family: var(--title-font);
  }
  .current-capacity {
    padding: 3px 6px;
    background: var(--button);
    cursor: pointer;
    border-radius: 8px;
    text-align: center;
    font-size: 12px;
    line-height: 18px;
    font-weight: 700;
    letter-spacing: 0;
    color: var(--light-text);
    display: -webkit-inline-box;
    display: inline-flex;
    -webkit-box-align: center;
    align-items: center;
  }
  .current-capacity i {
    color: var(--light-text);
    margin-left: 6px;
  }
  .horses-title {
    display: -webkit-inline-box;
    display: inline-flex;
    -webkit-box-align: center;
    font-family: var(--title-font);
    align-items: center;
    margin-bottom: 15px;
  }
  .horses-title h1 {
    margin-right: 8px;
    margin-bottom: 0;
    font-family: var(--title-font);
  }
  nav .pagination .page-item:not(.disabled) .page-link {
    background: var(--odd);
    color: var(--dark-text);
    border-radius: 30px;
  }
  .horse-item h2 {
    font-size: 16px;
    font-weight: 700;
    line-height: 24px;
    letter-spacing: 0;
    color: var(--dark-text);
    font-family: var(--title-font);
  }
  .horse-item .breed-and-age,
  .horse-item .tagline {
    font-size: 13px;
    line-height: 21px;
    display: block;
    letter-spacing: 0;
    color: var(--dark-text);
    font-family: var(--main-font);
  }
  .horse-item .sex-mare {
    background: #db346e00;
  }
  .horse-item .sex {
    font-size: 11px;
    font-weight: 600;
    line-height: 14px;
    position: absolute;
    z-index: 5;
    right: 3px;
    bottom: 3px;
    padding: 1px 4px;
    text-align: center;
    letter-spacing: 0.23px;
    text-transform: uppercase;
    color: #fafbff00;
    border-radius: 3px;
    background: #f17e6c00;
  }
  .horse-item .dam,
  .horse-item .foal {
    font-size: 11px;
    font-weight: 600;
    line-height: 14px;
    position: absolute;
    top: 3px;
    left: 3px;
    padding: 1px 4px;
    text-align: center;
    letter-spacing: 0.23px;
    text-transform: uppercase;
    color: #0000;
    border-radius: 3px;
    background: #fff0;
  }
  .horse-stats .horse-stat-energy i {
    color: var(--dark-text);
  }
  .horse-stats .horse-stat-fitness i {
    color: var(--dark-text);
  }
  .horse-stats .horse-stat-affection i {
    color: var(--dark-text);
  }

  img[src="https://www.horsereality.com/images/profilebg_africa.jpg"]
  {
    content: url(https://cdn.discordapp.com/attachments/987518421442891878/1010742499737874572/Background_1.png);
  }
  .minimap.africa {
    background-image: var(--map);
  }
  .minimap.asia {
    background-image: var(--map);
  }
  .minimap.australia {
    background-image: var(--map);
  }
  .minimap.europe {
    background-image: var(--map);
  }
  .minimap.south-america {
    background-image: var(--map);
  }
  .minimap.north-america {
    background-image: var(--map);
  }
  .horse-list .horse-item .image {
    display: flex;

    align-items: flex-end;

    justify-content: center;
    width: 110px;
    padding: 13px;
    background-repeat: no-repeat;
    background-position: -50px 100%;
    background-size: 160%;
    background-image: var(--pedigree) !important;
  }

  .horse-grid .horse-item .image {
    display: flex;

    align-items: flex-end;

    justify-content: center;
    height: 135px;
    padding-bottom: 18px;
    background-repeat: no-repeat;
    background-image: var(--pedigree) !important;
    background-position: -40px 87%;
    background-size: 150%;
  }
  .horse-stats .horse-stat--low span {
    color: #0d0c0c;
  }
  .button.secondary {
    background: var(--button);
  }
  .button.secondary:hover {
    background: var(--button-hover);
  }
  .modal-wrapper.active .modal-inner {
    -webkit-transform: scale(1);
    transform: scale(1);
    background: #30305c;
  }
  .modal-wrapper .modal-title {
    font-size: 12px;
    font-weight: 700;
    line-height: 18px;
    text-align: center;
    letter-spacing: 0;
    color: #000;
  }
  .horses .empty {
    padding: 30px;
    width: 100%;
    border: 1px solid #00000030;
    border-radius: 8px;
    display: -webkit-box;
    display: flex;
    -webkit-box-pack: center;
    justify-content: center;
    -webkit-box-align: center;
    align-items: center;
  }
  .stall-tabs-wrapper:after {
    position: absolute;
    z-index: 1;
    right: -15px;
    bottom: 0;
    left: -15px;
    height: 1px;
    content: "";
    background: #00000030;
  }

  .input-radio-group label .content {
    font-size: 13px;
    font-weight: 700;
    display: inline-block;
    width: 100%;
    padding: 5px 10px;
    cursor: pointer;
    -webkit-transition: all 0.2s ease-in-out;
    transition: all 0.2s ease-in-out;
    color: #fff;
    border-radius: 5px;
    background-color: var(--button);
  }
  .input-radio-group label .content:hover {
    color: var(--button-hover);
  }
  .text-blue {
    color: #ffffff;
  }

  .input-radio-group input[type="radio"]:checked + .content {
    color: #fff;
    background-color: var(--button);
  }

  .tabs .tab > span,
  .tabs .tab a,
  .tabs .tab button {
    font-size: 13px;
    font-weight: 700;
    line-height: 20px;
    display: block;
    padding: 6px 16px;
    white-space: nowrap;
    color: var(--button);
  }
  .tabs .tab.active {
    background: var(--odd);
  }
  .tabs-wrapper:after {
    position: absolute;
    z-index: 1;
    right: -15px;
    bottom: 0;
    left: -15px;
    height: 1px;
    content: "";
    background: var(--even);
  }
  .tabs .tab {
    position: relative;
    z-index: 2;
    margin-right: 3px;
    -webkit-transition: all 0.2s;
    transition: all 0.2s;
    border-radius: 8px 8px 0 0;
    background: var(--even);
  }
}

