@-moz-document url-prefix("https://v2.horsereality.com/")
{
  .header-cta {
    width: 100%;
    padding-left: 5px;
    padding-right: 5px;
    background-color: var(--even);
    text-align: center;
    z-index: 2;
    position: fixed;
    height: 40px;
    display: -webkit-box;
    display: flex;
    flex-wrap: nowrap;
    -webkit-box-pack: center;
    justify-content: center;
    opacity: 100%;
  }
  .header-cta .cta-button button {
    font-size: 13px;
    line-height: 18px;
    font-family: Roboto, sans-serif;
    font-weight: 600;
    border-radius: 2px;
    border: none;
    color: #000;
    background-color: var(--button);
    padding: 3px 8px;
  }
  .header-cta .cta-text .cta-description {
    font-size: 14px;
