    margin: 9px 0;
  }

  .gamestats a:link,
  .gamestats a:visited,
  .gamestats a:active {
    color: var(--light-text);
    text-decoration: none;
  }

  .container {
    width: 1130px;
    background-color: var(--background);
    margin-left: auto;
    margin-right: auto;
    padding-top: 50px;
  }

  .col_300 a:active,
  .col_300 a:link,
  .col_300 a:visited {
    color: var(--green-text);
    text-decoration: none;
    font-weight: 600;
    font-size: 13px;
    font-family: var(--main-font);
  }

  #forum .row_960.no_padding {
    padding: 0;
    width: 938px;
    background-color: var(--body);
  }

  button.green,
  input[type="submit"].green {
    background-color: var(--button);
    padding: 5px 20px;
  }

  button.green:hover,
  input[type="submit"].green:hover {
    background-color: var(--button-hover);
  }

  button.gray,
  input[type="submit"].gray {
    background-color: var(--button-hover);
    color: var(--white);
    padding: 5px 20px;
  }

  button.gray:hover,
  input[type="submit"].gray:hover {
    background-color: var(--button);
  }

  .numselected {
    color: var(--white);
    font-weight: 700;
    font-family: var(--main-font);
    padding: 5px 10px;
    background: var(--button);
    margin: 0 3px;
  }

  .npc_box {
    float: left;
    width: 940px;
    margin: 10px;
    background-color: var(--even);
    border: none;
    border-radius: 0px 20px 20px 0px;
  }

  .store_title {
    font-size: 14px;
    font-family: var(--main-font);
    text-align: center;
    font-weight: 700 !important;
    color: var(--green-text);
    margin-bottom: 15px;
  }

  .breadcrumbs a:link,
  .breadcrumbs a:visited,
  .breadcrumbs a:active {
    color: var(--green-text);
    text-decoration: none;
  }

  .grid_12 form fieldset label,
  .grid_12 fieldset label {
    float: left;
    width: 200px;
    margin-right: 10px;
    color: var(--green-text);
    font-size: 14px;
    font-family: var(--main-font);
    font-weight: 500;
  }

  .premium-only {
    border: none;
    border-radius: 20px 20px 20px 20px;
    background: var(--odd);
    opacity: 75%;
    padding: 8px;
  }

  .column_160 a:hover,
  .column_200 a:hover,
  .column_300 a:hover {
    color: var(--green-text);
  }

  .column_200 a:active,
  .column_200 a:link,
  .column_200 a:visited {
    color: var(--green-text);
    text-decoration: none;
  }

  .numbers {
    background: var(--boxes);
    padding: 5px 10px;
    margin: 0 3px;
    text-align: center;
  }

  .pagenumbers a:active,
  .pagenumbers a:link,
  .pagenumbers a:visited {
    color: var(--green-text);
    text-decoration: none;
  }

  table tr {
    background-color: var(--body);
  }

  .exchange_actions_window {
    display: inline-block;
    border: 1px solid var(--gold);
    width: 45%;
    padding: 8px;
  }

  .exchange_actions_window_buy h2 {
    border-bottom-color: none;
  }

  h2 {
    font-size: 18px;
    line-height: 21px;
    font-family: var(--main-font);
    font-weight: 400;
    color: var(--green-text);
    margin: 5px 0;
  }

  .exchange_actions_window_sell h2 {
    border-bottom-color: none;
  }

  button,
  input[type="submit"] {
    font-size: 13px;
    line-height: 18px;
    font-family: var(--main-font);
    font-weight: 600;
    -webkit-border-radius: 2px;
    -moz-border-radius: 2px;
    border-radius: 2px;
    border: none;
    padding: 3px 8px;
    color: var(--white);
    background-color: var(--button);
  }

  .exchange_books_window {
    vertical-align: top;
    display: inline-block;
    width: 45%;
    border: none;
    padding: 8px;
  }

  .exchange table thead th {
    padding: 5px 3px;
    text-align: left;
    background-color: var(--light-sage);
  }

  .exchange table td {
    border: none;
  }

  .exchange_books_window table tbody tr {
    background-color: var(--odd);
  }

  .exchange_books_window table tbody tr:nth-child(even) {
    background-color: var(--even);
  }

  .exchange_orders_window {
    vertical-align: top;
    display: inline-block;
    width: 45%;
    border: none;
    padding: 8px;
  }

  .pedigree {
    height: 620px;
    float: left;
    background-color: #0000;
    background-image: var(--pedigree);
  }

  body.background.africa {
    background-image: var(--background);
  }

  body.background.europe {
    background-image: var(--background);
  }

  body.background.australia {
    background-image: var(--background);
  }

  body.background.asia {
    background-image: var(--background);
  }

  body.background.north-america {
    background-image: var(--background);
  }

  body.background.south-america {
    background-image: var(--background);
  }

  .looking_at {
    background-color: var(--body);
    border: none;
    opacity: 75%;
    border-radius: 20px 20px 20px 20px;
    position: absolute;
    padding: 10px;
    margin-left: 280px;
    text-align: center;
    top: 20px;
    width: 640px;
  }

  .exchange_actions_window_sell h2 {
    border: none;
  }

  .exchange_actions_window_buy h2 {
    border: none;
  }

  .vet_blocks {
    float: left;
    width: 430px;
    margin-right: 20px;
    margin-bottom: 20px;
    background-color: var(--boxes);
    padding: 10px;
  }
}

