/* ==UserStyle==
@name         BitView Dark Theme
@version      20230615.01.28
@namespace    ?
==/UserStyle== */

@-moz-document domain("www.bitview.net") {
body {
    font-family: Arial,sans-serif;
    font-size: 12px;
    background-color: #252525;
    color: #EDEDED;
    margin: 0;
    overflow-y: scroll;
    word-break: break-word
}
h1,h2,h3,h4 {
  color: #EDEDED;
}
.signInBoxContent {
    border: 1px solid white;
    padding: 4px;
    background: #1A1A1A;
    text-align: center;
}
a:active, a:link, a:visited {
  color: white;
}
#masthead-search {
    float: left;
    padding: .15385em .23077em;
    margin: .23077em 0 0;
    line-height: 2.07692em;
    white-space: nowrap;
    background: #000000;
    border: 1px solid #ccc;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border-radius: 3px
}
.yt-menulink-primary .yt-button-primary {
    color: white
}
.yt-button-urgent,a.yt-button-urgent {
    font-weight: 700;
    color: white;
    background: black;
    border-color: gray
}
.yt-menubutton-primary .yt-menubutton-menu,.yt-menulink-primary .yt-menulink-menu {
    border-color: black;
    background-color: gray
}
.yt-menulink-menu a {
    border-bottom: 1px solid black
}

.yt-menulink-menu a:hover {
    background: black
}
.homepage-yellow-block {
    padding: 10px;
    background-color: #1A1A1A;
    border: 1px solid gray;
    border-radius: 6px;
    margin-bottom: 0
}
div[style*="color:#CC6600"]{
  color: gray !important;
}
.contest-box {
    text-align: center;
    background: #585858;
    padding: 10px;
    border: 1px solid #898989;
    border-radius: 6px
}
.homepage-block-heading {
    position: relative;
    font-size: 16px;
    font-weight: 700;
    padding: 2.5px 18px 4.5px 10px;
    background: #505050;
    border-bottom: 1px solid #ccc;
    border-radius: 5px 5px 0 0
}
.homepage-featured {
    border: 1px solid white;
    border-radius: 6px;
    margin-bottom: 10px
}
#homepage-featured-heading {
    border-bottom: 1px solid white;
  border-color: white;
    background-color: #181818;
    padding: 2.5px 18px 4.5px 10px;
    border-radius: 5px 5px 0 0 white;
}
#footer {
    clear: both;
    width: 960px;
    margin-top: 15px;
    margin-left: auto;
    margin-right: auto;
    font-size: 11px;
    line-height: 13px;
    background: #252525;
    border-top: 1px solid #ccc
}

.marT10,.marTmed,.topMar10 {
    padding: 10px;
    background-color: #252525
}
.browseGridView .vlfacets a {
    color: white
}
#header-left,#left-column {
    width: 190px;
    float: left;
    background-color: #505050;
    padding: 6px 0;
    border-radius: 6px
}
.searchFooterBox div {
    clear: both;
    text-align: left;
    font-size: 12px;
    background: #252525;
    border-top: 1px solid #ccc
}
.category-selected {
    font-weight: 700;
    padding-left: 11px !important;
    background-color: black
}
#left-column a {
    text-decoration: none;
    color: #fff;
}
.pagerCurrent {
    color: #fff;
    background-color: #252525;
    padding: 1px 3px;
    margin-right: 3px;
}
.signup-outer-frame {
    border-color: white;
    border-style: solid;
    border-width: 1px;
    padding: 4px;
    margin: 0 auto 35px auto;
    font-size: 13px;
}
.signup-inner-frame {
    background-color: #1A1A1A;
}
#watch-channel-vids-div {
    background: #1A1A1A;
    border: 1px solid white;
    margin-bottom: 10px white
}
#watch-channel-stats .watch-video-added {
    font-weight: 400;
    color: gray
}
.watch-tab-contents .watch-tab-sel {
    display: block;
    min-height: 32px;
    background: #1A1A1A;
    border-top: 1px solid #fff;
}
.watch-tabs .watch-tab-sel {
    background: #1A1A1A;
    border-bottom: 0;
    color: #000;
}
.watch-comment-head, .watch-comment-head-hidden {
    background-color: #252525;
    padding: 0 4px 4px;
}
.watch-comment-head[style*="background-color: #fffcc2;"] {
  background-color: darkkhaki !important;
}
#watch-some-stats .watch-stat {
    font-weight: 700;
    color: lightgray
}
.watch-comment-action a,.watch-comment-head-link {
    color: white;
    cursor: pointer;
}
.expand-panel a.expand-header,.expand-panel a.expand-header:link,.expand-panel a.expand-header:visited {
    color: lightgray;
    text-decoration: none;
    vertical-align: text-bottom
}
tr[style*="background: #f0f0f0;"]{
  background-color: #1A1A1A !important;
}
.settings {
    padding: 10px;
    background-color: #1A1A1A;
    border-bottom: 1px solid #9b9b9b;
    text-align: right;
}
#table #headings td {
    border-left: 1px solid #999;
    border-bottom: 1px solid #999;
    font-size: 11px;
    white-space: nowrap;
    text-overflow: ellipsis;
    background: #1A1A1A
}
#selected.sort-field {
    color: gray;
    font-weight: bold;
    cursor: text;
    text-decoration: none;
}
.video-description, .show-description, .channel-description {
    color: whitesmoke;
}
#facet-label {
    color: white;
}
.yt-button,a.yt-button {
    padding: 0 .8333em;
    white-space: nowrap;
    text-decoration: none;
    color: white;
    background: #303030;
    border: 1px solid white;
    -webkit-border-radius: 3px;
    border-radius: 3px;
    cursor: pointer;
    display: inline-block;
    *display: inline
}
#view-pane .actions {
    padding: 10px;
    width: auto;
    background-color: #353535;
    border-bottom: 1px solid #999
}
#view-pane .footer {
    border-top: 1px solid #999;
    background-color: #1A1A1A;
    padding: 4px 10px;
    margin: 0;
    border-left: 1px solid #999;
    text-align: left;
}
#list-pane .folder.selected {
    background: #1A1A1A;
}
#list-pane a {
    display: block;
    padding: 4px 10px;
    font-weight: 700;
    color: lightgray;
    font-size: 12px;
}
#view-pane .splitter {
    padding-left: 7px;
    border: 1px solid #999;
    background-color: #454545;
}
div[style*="text-decoration: none;background-color:#d6e1f5;"]{
  background-color: black !important;
}
.yt-menulink-primary .yt-menulink-menu li a {
    color: white;
    background: gray;
    font-weight: 700
}
.yt-menubutton-primary .yt-menubutton-menu li a {
    color: white;
    background: gray;
    font-weight: 700;
}
.yt-menubutton-menu li, .yt-menulink-menu li {
    border-bottom: 1px solid #000000;
    font-size: 12px;
}
.yt-menubutton-menu li a:hover,.yt-menubutton-primary .yt-menubutton-menu li a:hover,.yt-menulink-menu li a:hover,.yt-menulink-primary .yt-menulink-menu li a:hover {
    background: #000
}
a[style*="background:transparent url(/img/mmgrads-vfl38740.gif) repeat-x scroll 0 -24px;"]{
  background: #1A1A1A !important;
}
tr[style*="background:white;"]{
  background: lightgray !important;
}
#messages_in td,#messages_in td a {
    color: graytext
}
table[style*="background-color: #FFFFFF"]{
  background-color: #252525 !important;
}
span[style="font-size: 12px; font-weight: normal; color: #000;"]{
  color: gray !important;
}
.watch-owner-options {
    background: #1a1a1a;
    border: 1px solid #ffffff;
    padding: 6px;
    height: 50px;
    position: relative;
    bottom: 11px
}
.show-more a,.show-less a {
    font-weight: 400!important;
    color: #fff!important
}
a[style*="color:#03c; cursor:pointer;"]{
    color: white !important;
}
.action-button .action-button-text,.add-button .add-button-text,.edit-button .edit-button-text {
    font-family: Arial,sans-serif;
    font-size: 12px;
    font-weight: 700;
    float: left;
    display: block;
    color: #ffffff;
    text-decoration: none;
    background: #000000;
    border: 1px solid #ffffff;
    height: 18px;
    padding: 5px 10px 0;
    border-radius: 3px
}
.welcome-head {
    padding: 6px;
    background: #303030;
    border-bottom: 1px solid #fff;
    border-radius: 5px 5px 0 0
}
.headerTitle {
    font-size: 14px;
    font-weight: 700;
    color: #fff
}
a[style*="background-color:#d6e1f5;"]{
    background-color: #1A1A1A !important ;
}
div[style*="background-color:#d6e1f5;"]{
    background-color: #111 !important;
}
.subcategory#selected {
    background: #606060;
    padding-left: 7px
}

#selected a {
    color: #fff;
    cursor: text;
    text-decoration: none!important
}

.subcategory-left-cap {
    width: 10px;
    height: 38px;
    float: left;
    margin-top: -12px;
    background: #606060 ;
    display: none
}

.subcategory-right-cap {
    width: 10px;
    height: 38px;
    float: left;
    margin-top: -12px;
    background: #606060;
    display: none
}
.videoModifiers {
    background: #303030;
    border: 0;
    border-radius: 8px;
    height: 19px;
    float: right;
    width: 750px;
    padding: 7px 0px 6px 0px;
    border-bottom: 1px solid #252525;
    text-align: left;
}
.dropdown-content {
    display: none;
    position: absolute;
    background-color: gray;
    border: 1px solid #000000;
    z-index: 99;
    margin-top: 16px;
    left: 0;
    top: 0;
    line-height: 20px;
    width: auto;
    text-align: left;
    border-top: 0;
    margin-left: 13px
}

.dropdown-content a:hover {
    background-color: #000
}
div[style*="background-color: #dee0fd;padding: 10px;border-radius: 8px;margin-top: 10px;width: 540px;"]{
    background-color: #1A1A1A !important;
}
.uploaderBox {
    background-color: #1a1a1a;
    padding: 10px;
    border-radius: 8px;
    margin-top: 10px;
    width: 540px;
}
a[style*="font-size: 11px; float: right;color: #03C;text-decoration: underline;cursor:pointer;"]{
    color: white !important;
}
a[style="position:absolute;right: 0;top: 0;font-size: 11px;float: right;color: #03C;text-decoration: underline;cursor: pointer;"]{
    color: white !important;
}
td[bgcolor="#FFF"]{
    background-color: #252525 !important;
}
tr[bgcolor="#FFF"]{
    background-color: #151515 !important;
}
span[style="font-weight:bold;background-color:#FFF;padding:1px 4px 1px 4px;border:1px solid #999;margin-right:5px"]{
    background-color: #151515 !important;
}
.pagingDiv {
    background: #151515;
    padding: 4px
}
div[style="margin-left: 10px; margin-right: 10px;background:white;border-top:0;box-sizing:border-box;padding:5px"]{
    background-color: #151515 !important;
}
.video-more {
    color: #fff;
    font-weight: 700;
    font-size: 14px;
    float: left;
    height: 25px;
    margin-top: -12px;
    padding: 13px 13px 0;
    cursor: pointer
}
.dropdown-content a {
    display: block;
    color: #fff;
    font-weight: 700;
    text-decoration: none;
    padding: 1px 10px;
    white-space: nowrap;
    border-top: 1px solid #000;
    font-size: 12px
}
#masthead .logo {
    content: url(https://i.imgur.com/I1nGQkC.png) ;
    float: left;
    display: block;
    margin: 6px 6px 6px 0;
    width: 120px;
    height: 50px;
}
a.yt-menulink-btn.yt-button.yt-button-primary {
    bottom: 2px;
    position: relative;
    font-weight: 700;
    color: #fff!important
}
a[style*="color: #03c!important;"]{
    color: white !important;
}
div[style*="float:left;width: 758px;background: #EAEAEA;border: 1px solid #999;"]{
    background-color: black !important;
}
div[style*="padding:10px; background: #eaeaea;border-bottom: 1px solid #999;"]{
    background: #3c3c3c !important;
}
div[style*="background:white;padding-top:3px"]{
    background: #252525 !important;
}
a[style*="position: absolute; text-decoration: none; color: rgb(0, 0, 0); font-family: Verdana; font-size: 11px; opacity: 0.7; display: block; left: 75px; top: 25px;"]{
    color: #ededed !important;
}
text[fill="#000000"]{
    fill: white !important;
}
#search-section-header .name {
    color: #fff;
    float: left
}
#search-options-container .search-sort span {
    color: #fff
}
.yt-menulink .yt-menulink-btn {
    display: inline;
    padding: 0;
    border: none;
    background: 0 0;
    color: #fff
}
.yt-menubutton-menu li a,.yt-menulink-menu li a {
    display: block;
    padding: .4166em .6666em;
    color: #000;
    background: gray
}
.yt-menulink-menu li a {
    color: #fff;
    border-bottom: 0;
    font-weight: 400
}
.yt-menubutton-menu,.yt-menulink-menu {
    position: absolute;
    top: 1.5em;
    left: .25em;
    z-index: 99;
    min-width: 100%;
    border: 1px solid #000;
    visibility: hidden
}
.yt-menubutton-menu,.yt-menulink-menu {
    background: #000
}
}
