style.css:
@-moz-document domain("music.youtube.com") {
/* MINIPLAYER */
    ytmusic-player[player-ui-state_="MINIPLAYER"],
    ytmusic-player[player-ui-state="MINIPLAYER"] {
        --ytmusic-mini-player-height: 320px;
        margin: 0 !important;
    }

    /* PLAYER */
    .content.style-scope.ytmusic-player-page {
        padding: 0.25rem;
    }

    ytmusic-player-page:not([is-mweb-modernization-enabled]) .content.ytmusic-player-page {
        padding: 0;
    }

    video.video-stream.html5-main-video {
        /* width: 100% !important; */
        top: 150px;
    }

    .content.style-scope.ytmusic-player-page>.main-panel {
        padding: 0;
    }

    ytmusic-player#player[player-ui-state_="PLAYER_PAGE_OPEN"],
    ytmusic-player#player[player-ui-state="PLAYER_PAGE_OPEN"] {
        max-width: 100% !important;
        width: 100% !important;
        margin: 0 !important;
        height: 100%;
    }

    .side-panel.modular.style-scope.ytmusic-player-page {
        margin-left: 0;
        width: 26vw;
    }

    ytmusic-player-page:not([video-mode_]):not([player-fullscreened_]) #player.ytmusic-player-page {
        max-width: 100%;
        margin: 0 !important;
    }

    /* PLAYER QUEUE (AT FULLSCREEN PLAYER) */
    .autoplay.ytmusic-player-queue {
        display: none;
    }

    .autoplay.ytmusic-tab-renderer {
        margin: 0;
    }

    tp-yt-paper-tabs.tab-header-container.style-scope.ytmusic-player-page {
        height: 42px;
    }

    /* NAVBAR */
    ytmusic-logo.style-scope.ytmusic-nav-bar {
        transform: scale(1.3);
        margin-left: 3rem;
    }

    div.style-scope.ytmusic-app-layout#nav-bar-divider {
        top: 5vh;
    }

    ytmusic-app-layout#layout>ytmusic-nav-bar.style-scope.ytmusic-app {
        height: 5vh;
        z-index: 10;
    }

    ytmusic-app-layout#layout>div#nav-bar-background {
        height: 5vh;
    }

    ytmusic-app-layout#layout {
        height: 5vh;
    }

    .header-group.ytmusic-carousel-shelf-renderer {
        width: 95%;
        max-width: 100%;
    }

    ytmusic-settings-button.settings-button.style-scope.ytmusic-nav-bar {
        transform: scale(1.3);
    }

    ytmusic-cast-button.cast-button.style-scope.ytmusic-nav-bar {
        display: none;
    }

    /* SIDEBAR LINKS STYLE */
    ytmusic-guide-entry-renderer[is-collapsed] yt-icon.ytmusic-guide-entry-renderer {
        display: none;
    }

    ytmusic-guide-entry-renderer[is-collapsed] .title.ytmusic-guide-entry-renderer {
        font-size: 24px;
        line-height: 42px;
    }

    ytmusic-guide-entry-renderer[active] tp-yt-paper-item.ytmusic-guide-entry-renderer {
        background-color: rgba(0, 0, 0, 0);
    }

    tp-yt-paper-item.ytmusic-guide-entry-renderer:hover {
        background-color: rgba(0, 0, 0, 0);
    }

    ytmusic-guide-entry-renderer[is-collapsed] .title.ytmusic-guide-entry-renderer {
        color: rgba(255, 255, 255, 0.5);
        font-weight: 500;
    }

    ytmusic-guide-entry-renderer.style-scope.ytmusic-guide-section-renderer[active] yt-formatted-string.ytmusic-guide-entry-renderer,
    ytmusic-guide-entry-renderer[is-collapsed] .title.ytmusic-guide-entry-renderer:hover {
        color: rgba(255, 255, 255, 1);
    }

    /* SIDEBAR MOVED TO HEADER (CONSIDER THAT SIDEBAR SHOULD BE COLLAPSED SINCE ITS BUTTON IN HEADER WILL BE HIDDEN)*/
    #content-wrapper.ytmusic-browse-response {
        margin-left: 0;
    }

    ytmusic-player-page#player-page {
        left: 0px;
        width: 100%;
        top: 5vh;
        height: 89.5vh;
    }

    tp-yt-app-drawer#guide {
        height: 5vh;
    }

    div.style-scope.ytmusic-app-layout#mini-guide-background {
        height: 5vh;
    }

    div.style-scope.ytmusic-app#mini-guide {
        height: 5vh;
        width: 35%;
        margin-left: 55vw;
        z-index: 11;
    }

    div#mini-guide-spacer {
        margin: 0;
    }

    ytmusic-app[is-bauhaus-sidenav-enabled] #guide-spacer.ytmusic-app,
    ytmusic-app[is-bauhaus-sidenav-enabled] #mini-guide-spacer.ytmusic-app {
        margin-top: 0;
    }

    ytmusic-guide-renderer#mini-guide-renderer {
        width: 100%;
        height: 5vh;
        padding: 0;
    }

    #items.ytmusic-guide-section-renderer {
        display: flex;
        flex-direction: row;
        margin-top: 2px;
    }

    ytmusic-guide-entry-renderer.style-scope.ytmusic-guide-section-renderer {
        width: 6vw;
        margin-top: 2px;
        height: 42px;
        margin-right: 2vw;
    }

    ytmusic-guide-entry-renderer[is-collapsed] tp-yt-paper-item.ytmusic-guide-entry-renderer {
        padding: 0;
        height: 42px;
    }

    ytmusic-guide-entry-renderer.style-scope.ytmusic-guide-section-renderer:nth-child(4) {
        display: none;
    }

    ytmusic-nav-bar[is-bauhaus-sidenav-enabled] .left-content.ytmusic-nav-bar yt-icon-button.ytmusic-nav-bar {
        display: none;
    }

    /* PLAYER BAR */
    ytmusic-app-layout[player-visible]>[slot=player-bar],
    ytmusic-app-layout[player-visible] #player-bar-background.ytmusic-app-layout {
        height: 5.5vh;
    }

    /* HOME SCREEN */
    ytmusic-chip-cloud-renderer.ytmusic-section-list-renderer {
        width: 95%;
        max-width: 100%;
    }

    #items.ytmusic-carousel {
        width: 95%;
    }

    /* OTHER RESOLUTIONS */
    @media (min-width: 1578px) {
        ytmusic-nav-bar[is-bauhaus-sidenav-enabled] .right-content.ytmusic-nav-bar {
            right: 3rem;
        }
    }

    @media (max-width: 1200px) {
        div.style-scope.ytmusic-app#mini-guide {
            margin-left: 65vw;
        }

        ytmusic-guide-entry-renderer[is-collapsed] .title.ytmusic-guide-entry-renderer {
            font-size: 16px;
        }

        ytmusic-guide-entry-renderer.style-scope.ytmusic-guide-section-renderer {
            margin-right: 0.5vw;
        }
    }

    @media (max-width: 935px) {

        .side-panel.modular.style-scope.ytmusic-player-page {
            width: 100%;
            max-width: 100%;
        }

        #steering-chips.ytmusic-player-queue {
            max-width: 100%;
        }

        div.style-scope.ytmusic-app#mini-guide {
            margin-left: 20vw;
        }

        ytmusic-guide-entry-renderer.style-scope.ytmusic-guide-section-renderer {
            margin-right: 3vw;
        }

        ytmusic-guide-entry-renderer[is-collapsed] .title.ytmusic-guide-entry-renderer {
            font-size: 20px;
        }

        ytmusic-guide-entry-renderer[is-collapsed] yt-icon.ytmusic-guide-entry-renderer {
            display: inline-flex;
            margin-top: 1vh;
        }

        .title-column.style-scope.ytmusic-guide-entry-renderer {
            display: none;
        }

        #items.ytmusic-carousel .ytmusic-carousel:first-child,
        ytmusic-carousel[num-items-per-column="2"] #items.ytmusic-carousel .ytmusic-carousel:nth-child(-n+2).ytmusic-carousel,
        ytmusic-carousel[num-items-per-column="3"] #items.ytmusic-carousel .ytmusic-carousel:nth-child(-n+3).ytmusic-carousel,
        ytmusic-carousel[num-items-per-column="4"] #items.ytmusic-carousel .ytmusic-carousel:nth-child(-n+4).ytmusic-carousel,
        ytmusic-carousel[num-items-per-column="5"] #items.ytmusic-carousel .ytmusic-carousel:nth-child(-n+5).ytmusic-carousel {
            padding-left: 0;
        }

        ytmusic-logo.style-scope.ytmusic-nav-bar {
            transform: scale(1);
            margin-left: 1rem;
        }

        /* throws parser error on stylish, apply using another chrome extension */
        /*
        ytmusic-nav-bar.style-scope.ytmusic-app:has(div#right-content > ytmusic-search-box.style-scope.ytmusic-nav-bar[opened]) {
            z-index: 12;
        }
        */
    }

    @media (min-width: 616px) and (max-width: 1149px) {

        ytmusic-player[player-ui-state_=MINIPLAYER],
        ytmusic-player[player-ui-state=MINIPLAYER] {
            --ytmusic-mini-player-height: 240px;
        }
    }

    @media (max-width: 616px) {

        ytmusic-player[player-ui-state_=MINIPLAYER],
        ytmusic-player[player-ui-state=MINIPLAYER] {
            --ytmusic-mini-player-height: 180px;
        }
    }

    @media (max-width: 1149px) {
        ytmusic-nav-bar[is-bauhaus-sidenav-enabled][guide-collapsed] .center-content.ytmusic-nav-bar {
            width: 100%;
            padding-left: 2vw;
            justify-content: left;
        }

        ytmusic-nav-bar[is-bauhaus-sidenav-enabled] ytmusic-search-box.ytmusic-nav-bar {
            width: 70%;
        }
    }

    @media (max-width: 615px) {
        ytmusic-logo.style-scope.ytmusic-nav-bar {
            transform: scale(0.8);
            margin-left: 0;
        }

        ytmusic-app[is-bauhaus-sidenav-enabled] #mini-guide.ytmusic-app {
            display: block;
            margin-left: 25vw;
        }
    }

    @media (max-width: 380px) {
        ytmusic-logo.style-scope.ytmusic-nav-bar {
            margin-left: 0;
        }

        ytmusic-app[is-bauhaus-sidenav-enabled] #mini-guide.ytmusic-app {
            width: min-content;
            margin-left: 7rem;
        }

        ytmusic-nav-bar[is-bauhaus-sidenav-enabled][guide-collapsed] .left-content.ytmusic-nav-bar {
            padding-left: 0;
        }

        #items.ytmusic-guide-section-renderer {
            justify-content: space-around;
        }
    }
}
