style.css:
@-moz-document domain("web.roblox.com"), domain("roblox.com"), domain("www.roblox.com") {
body:after {

  
  /*change the "block" to "none"*/
  display: block;

  
  content: "Hey! creator of 'Cooler roblox gui here', please get my new modernized roblox theme! its way cooler.";
  position: fixed !important;
  z-index: 999999999;
  top: 100%;
  transform: translateY(-100%);
  background: black;
  padding: 2px;
  padding-right: 8px;
  border-top-right-radius: 5px;
  pointer-events: none;
}

/*home page games*/
.game-carousel, .hlist, .section-content, .game-grid {
  overflow: visible;
}
.game-carousel .game-card-container, .hlist .list-item, .game-grid .game-card-container {
  transition: 0.2s;
  user-select: none;
	border-radius: 10px;
}
.hlist.game-cards > .list-item {
  padding-right: 0px !important;
  width: 150px !important;
  margin-right: 10px !important;
}
.game-carousel .game-card-container:hover, .hlist .list-item:hover, .game-grid .game-card-container:hover {
  box-shadow: 0px 12px 20px -10px black;
  transform: translateY(-5px);
}
.game-carousel .game-card-container:active, .hlist .list-item:active, .game-grid .game-card-container:active {
  transform: translateY(5px);
  box-shadow: none;
  transition: 0.1s;
}
group-store-item.ng-scope.ng-isolate-scope {
    display: inline-block;
}
group-store-item li.list-item.item-card {
    width: 131px;
    margin-right: 30px;
}
.hlist.avatar-cards li {
  transform: none !important;
  box-shadow: none !important;
}
.list-item.real-game-pass {
    box-shadow: none !important;
    transform: none !important;
}
/*friend activity box*/
.game-card-friend-activity {
  margin: 5px;
  margin-top: 0px;
  margin-bottom: 0px;
  border-radius: 5px;
  transform: translateY(-3px);
}

/*leftside menu*/
.left-col-list li {
  border-radius: 10px;
  padding: 5px;
  margin: 0px;
}
.left-col-list li:hover {
  background-color: #00000080;
}
.left-col-list li:active {
  transform: translateY(2px);
}

/*top banner links*/
ul.nav.rbx-navbar.hidden-xs.hidden-sm.col-md-5.col-lg-4 li:hover {
  background-color: #00000080;
}
ul.nav.rbx-navbar.hidden-xs.hidden-sm.col-md-5.col-lg-4 li:active {
  padding-top: 2px;
}

/*buttons*/
button:hover {
  filter: brightness(1.1);
}
button:active {
  filter: brightness(0.7);
  transform: translateY(2px);
}

/*catalog filters*/
.topic:hover {
  filter: brightness(1.2);
}
.topic:active {
  filter: brightness(0.8);
}

/*detached sections border*/
.section-content {
  border-radius: 7px;
}

/*things that the avatar is wearing on the profile page*/
.accoutrement-items-container .accoutrement-item {
  transition: 0.2s;
}
.accoutrement-items-container .accoutrement-item:hover {
  transform: translateY(-5px);
}
.accoutrement-items-container .accoutrement-item:active {
  transform: translateY(5px);
}
/*makes the "see all" thingy look cooler*/
.see-all-link-icon, .see-all-link-icon:after {
  transition: 0.2s;
}
.see-all-link-icon:hover {
  transform: translateX(10px);
}
.see-all-link-icon:hover:after {
  opacity: 0;
}
}
