  {
    box-shadow: none !important;
  }

/*misc*/
  .container-1D34oG,  /*friends list*/
.scrollbar-2rkZSL,  /*scrollbar*/
.pad-29zQak,
.track-1JN30G
  {
    background: transparent !important;
  }

/* ------SCROLLBAR------ */
  .scrollbar-2rkZSL
  {
    width: 8px;
  }

  .thumb-2JwNFC
  {
    background: rgba(0, 0, 0, .5) !important;
  }

  .track-1JN30G,
.thumb-2JwNFC
  {
