style.css:
@-moz-document domain('mcpehub.org'), url-prefix('http://mcpehub.org/') {

/* It is not production code. Don't use it in your own project. */
/* Stylish don't support css variables :( */

/* page layout */

header, nav>.flex {
    background: color-mix(in srgb, black, gray) !important;
}

nav>.flex {
    border-bottom: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 1px solid !important;
}

body,
.layout {
    background: color-mix(in srgb, black, dimgray) !important;
}

a, h1, h2, h3, h4, h5, button, ul, ol, li, p, span, div, time {
    color: color-mix(in srgb, white, whitesmoke) !important;
}

a:focus-visible, button:focus-visible, input:focus-visible, textarea:focus-visible {
    outline: color-mix(in srgb, darkseagreen 80%, green) 2px solid !important;
}

.layout .content>.row>.col-md-8 {
    border-right: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 1px solid !important;
}

/* inputs */

header .search-form>div>input {
    background: color-mix(in srgb, black, darkgray);
    border-radius: 10px;
    color: color-mix(in srgb, white, whitesmoke);
}

header .search-form {
    border-radius: 0px;
}

header .search-form>div {
    background: color-mix(in srgb, black, gray) !important;
}

/* buttons */

.center.medium.green {
    background: color-mix(in srgb, color-mix(in srgb, black, gray), lightgreen 15%) !important;
}

/* cards */

.news-item {
    background: color-mix(in srgb, black, gray);
}

.news-item .flex.middle.end span {
    filter: invert(1) brightness(10);
    color: rgba(0,0,0,.38) !important;
}

.flex.between.regular.transparent-grey>span {
    opacity: .38;
}

.news-item .flex.middle.end span.liked {
    filter: none;
}

/* footer */

.prefooter, footer {
    background: color-mix(in srgb, black, gray);
    border-bottom: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 1px solid !important;
}

.socials svg {
    filter: invert(1);
}

.socials svg:hover,
.socials svg:focus {
    filter: none;
}

/* pages navigation */

.navigation>div {
    background: color-mix(in srgb, black, gray);
    color: color-mix(in srgb, white, whitesmoke);
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.navigation>div>span,
.navigation>div>a {
    filter: invert(1)
}

.navigation>div>div>*:not(:last-child),
.navigation>div>*:not(:last-child) {
    border-right: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.image img {
    border-radius: 6px !important;
}

/* Article content */

.col-xs-12.col-md-8 {
    border-right: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) !important;
}

.skins-item>.skin-image {
    border-bottom: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) !important;
}

.grey.regular>.easylike_count {
    background: none !important;
}

.fullstory-content {
    color: color-mix(in srgb, white, whitesmoke) !important;
}

.fullstory-content span {
    background: color-mix(in srgb, darkseagreen 80%, green) !important;
}

.fullstory-content span {
    background: color-mix(in srgb, darkseagreen 80%, green) !important;
}

/* Stylish don't support :not :( */

.fullstory .content-box>.download-item>.item-content>.flex-sm a>div>span {
    background: none !important;
}

.fullstory .content-box>.download-item>.item-content>.flex-sm a>div>span>span, .fullstory .content-box>.download-item>.item-content>.flex-sm a>div>span>i {
    background: url("data:image/svg+xml;base64,") !important;
}

.fullstory-support-v {
    background-color: color-mix(in srgb, black, gray) !important;
}

.fullstory .fullstory-support-v>.support-text {
    color: color-mix(in srgb, white, whitesmoke) !important;
}

.fullstory .fullstory-support-v p {
    background: color-mix(in srgb, color-mix(in srgb, black, gray), lightgreen 15%) !important;
}

.fullstory-author-info {
    background-color: color-mix(in srgb, black, gray) !important;
}

.fullstory img {
    border-radius: 6px !important;
}

.fullstory .related {
    border-top: 1px solid color-mix(in srgb, black, gray);
}

.fullstory .title_spoiler,
.fullstory .text_spoiler {
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
    color: color-mix(in srgb, white, whitesmoke) !important;
}

.fullstory .text_spoiler {
    border-top: none !important;
}

.fullstory .title_spoiler a {
    color: inherit !important;
}

.fullstory .title_spoiler::after {
    filter: invert(1);
}

/* Comments */

.comments-wrapper {
    background: color-mix(in srgb, black, gray) !important;
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.comments-wrapper input,
.comments-wrapper textarea {
    background: color-mix(in srgb, black, darkgray) !important;
    color: color-mix(in srgb, white, whitesmoke) !important;
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.comments-wrapper .comment {
    border-top: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.comments-tree-list {
    border-left: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

/* Fixed download button */

.fixed-download-btn {
    background: color-mix(in srgb, black, gray) !important;
}

.fixed-download-btn button {
    background: color-mix(in srgb, color-mix(in srgb, black, gray), lightgreen 15%) !important;
}

/* Article icons */

.icon.reply,
.icon.views,
.icon.comment-icon,
.regular.transparent-grey>.easylike_count,
.icon.comment-icon ~ span,
.icon.views ~ span,
.icon.navigation-left,
.icon.navigation-right,
.icon.back-icon,
.icon.cube {
    filter: invert(1) !important;
}

.regular.transparent-grey>.easylike_count,
.icon.comment-icon ~ span,
.icon.views ~ span {
    color: color-mix(in srgb, black, dimgray) !important;
    font-weight: 500;
}

/* Filters */

.subcats a {
    background: color-mix(in srgb, black, gray) !important;
}

.news_set_sort__active,
.sort {
    background: color-mix(in srgb, black, gray) !important;
}

.news_set_sort__active::after {
    filter: invert(1);
}

.sort span:hover,
.sort span:focus {
    background: color-mix(in srgb, black, darkgray) !important;
}

input {
    background: color-mix(in srgb, black, darkgray) !important;
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) !important;
    color: color-mix(in srgb, white, whitesmoke) !important;
}

/* Download panel */

.b-actual-v {
    background: color-mix(in srgb, black, gray);
}

.b-actual-v .b-actual-v__links .list>.item:nth-child(odd) {
    border-right: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.b-actual-v .b-actual-v__links {
    border-top: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;
}

.download-item.selectFull,
.download-item {
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 2px solid !important;   
    background: color-mix(in srgb, black, gray) !important;
}

.download-item.selectFull .body {
    background: color-mix(in srgb, black, gray);
}

.download-item .body {
    background: color-mix(in srgb, black, gray);
}

.downloads-nav>.downloads-slider::after {
    background: linear-gradient(to right, transparent, color-mix(in srgb, black, dimgray));
}

/* Card styles */

.news-item .news-item-info>.build-tags>.flex>div {
    border: color-mix(in srgb, color-mix(in srgb, black, gray), dimgray) 1px solid;
}

/* Download */

.download-content .news-data.flex-sm.middle {
    background: color-mix(in srgb, black, gray);
}

.btn.green-bg.disabled {
    background: color-mix(in srgb, black, darkgray) !important;
}
}
