style.css:
@-moz-document domain("slashdot.org") {
/* SlashdotSlim
   On Domain slashdot.org
   You need to edit the style and save for it to kick in.
   There is a vertical bar that lingers, but it's the best I could do.
*/

.main-wrap.has-rail-right > .main-content {
    margin-right: 0;
}

.rail-right {
	width: 0;
	/* margin-left: 0; */
}

#firehose, #firehose-mq {
    background-color: #444;
    color: #ddd;
}

.article:not(.fhitem-journal) .body .p {
    color: #ddd;
}

.article .body .p a, .commentBody a, .bodytext a {
    color: rgb(8, 177, 177);
}

a:visited, a:hover {
    color: rgb(8, 177, 177);
}

}
