/* ==UserStyle==
@name          Mega Man X - Twitter Icon
@author        Hooded Edge
@description   Replaces Twitter Icon with Mega Man X's Extra Life Sprite.
@version       1.0
==/UserStyle== */

@-moz-document url-prefix("https://twitter.com") {
     /* Remove Default Twitter / X Icon */
     a[aria-label = "Twitter"] svg[aria-hidden = "true"] {
          display: none;
     }

     /* Add Mega Man X Life Icon */
     a[aria-label = "Twitter"] {

          /* Image */
          content: url("https://pbs.twimg.com/media/F2oPtAqWEAEk5gT?format=png&name=240x240") !important;

          width: 48px;
          height: 48px;
          object-fit: cover;

          image-rendering: pixelated;
     }
}
