style.css:
@-moz-document domain("www.dlsite.com"){

    /* Enable select title text*/
    #work_name {
      user-select: text !important;
    }

    /* Show alerady purchased even if not hovered */
    .work_operation_btn {
      display: block;
      position: relative !important;
      opacity: 1 !important;
    }

    .work_deals.work_labels {
      margin-top: 8px !important;
    }

    .n_worklist .work_img_main {
      height: auto !important;
    }

    .search_result_img_box_inner  {
      display: flex;
      flex-direction: column;
    }

    .work_img_main {
      flex-grow: 1;
    }

    dd.work_deals.work_labels{
      margin-top: auto !important;
    }

    .work_btn_box {
      align-self: flex-end;
      width:100%;
}

}
