style.css:
{"result":"@-moz-document domain("wikipedia.org")
{
	table[class*=" mw-collapsed"] tr[style] * {
	  line-height: 0px !important;
	  margin: 0px !important;
	  padding: 0px !important;
	  opacity: 0 !important;
	  transition:
	    opacity 0.1s 0s ease,
	    line-height 0.2s 0.2s ease
	    !important;
	}

	table:not([class*=" mw-collapsed"]) tr[style] * {
	  line-height: auto !important;
	  margin: initial !important;
	  padding: initial !important;
	  opacity: 1 !important;
	  transition: 
	    line-height 0.2s 0s ease, 
	    opacity 0.1s 0.4s ease 
	    !important;
	}

  tr {display: table-row !important;}

  th, td {display: table-cell !important;}
}
"}
