style.css:
/* User Stylesheet for Better Readability on mjrn.net */

@-moz-document domain("mjrnet.org"), url-prefix("http://mjrnet.org/"), url-prefix("https://mjrnet.org/") {
  /* Set font family and size for the entire page */
  body {
    font-family: Arial, sans-serif;
    font-size: 16px;
    line-height: 1.6;
  }

  /* Set link styles */
  a {
    color: #007BFF !important;
    text-decoration: none !important;
  }

  a:hover {
    text-decoration: underline !important;
  }

  /* Set heading sizes */
  h1 {
    font-size: 24px !important;
  }

  h2 {
    font-size: 20px !important;
  }
}
