style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("messenger.com") {
    /* Couleurs */
    :root {
        --primary-color: #3b2504;
        --secondary-color: #f4a460;
        --tertiary-color: #8b4513;
    }

    /* Arrière-plan */
    body {
        background-image: url("https://i.imgur.com/https://gumlet.assettype.com/swarajya/2017-05/549eacea-2045-4a2d-9a41-a67f23098b4a/TOD.png?rect=0%2C32%2C1043%2C548&w=1200&auto=format%2Ccompress&ogImage=trueaboveaboveaboveaboveabove.jpg");
        background-size: cover;
        background-repeat: no-repeat;
    }

    /* En-tête */
    ._1enh {
        background-color: var(--secondary-color) !important;
        color: var(--primary-color) !important;
    }

    /* Messages */
    ._4sp8, .clearfix._o46._3erg._29_7.direction_ltr.text_align_ltr {
        background-color: var(--tertiary-color) !important;
        color: white !important;
    }

    /* Boutons */
    ._4jy0, ._30yy, ._4jy3, ._4jy1 {
        background-color: var(--secondary-color) !important;
        color: var(--primary-color) !important;
    }
}

/* Paramètres Stylish */
@-moz-document regexp("https?://userstyles.org/styles/.*") {
  :root {
    --stylish-install-link: "https://userstyles.org/styles/your-style-id-here";
  }
  #install-button:before {
    content: "Installer";
  }
  #install-button:after {
    content: "Indiana Jones Theme for Facebook Messenger";
  }
}
