style.css:
/* by PlayingWithoutCredit (Roblox) */

@-moz-document domain("roblox.com") {
.dark-theme ::selection {
  background: white;
  color: black;
}

body {
  transition: 0.5s;
}

button {
  transition: 0.3s;
}

button:hover {
  transform: scale(0.95, 0.95);
}

button:active {
  transform: scale(0.9, 0.9);
}

.dynamic-overflow-container {
  transition: 0.3s;
  padding: 1px;
  border-radius: 1rem;
}

.dynamic-overflow-container:hover {
  transition: 0.3s;
  background: rgba(255, 255, 255, 0.5);
  border-radius: 0.5rem;
}

a {
  transition: 0.3s;
  text-decoration: none;
}

a:hover {
  transform: scale(0.95, 0.95);
}

a:active {
  transform: scale(0.9, 0.9);
}

ul {
  text-decoration: none;
}

li {
  transition: 0.3s;
  text-decoration: none;
}

li:hover {
  transform: scale(0.95, 0.95);
}

li:active {
  transform: scale(0.9, 0.9);
}

.game-card-container {
  transition: 0.3s;
  border-radius: 1rem;
  padding: 4.5px;
}

.game-card-thumb-container {
  border-radius: 1rem;
	draggable: false;
  align-items: center;
}

.container-head {
  transition: 0.5s;
  position: absolute;
}

.game-card-container:hover {
	transform: scale(0.95, 0.95);
  background: rgba(255, 255, 255, 0.2);
  box-shadow: 0px 0px 50px 20px rgba(0, 0, 0, 0.5);
}

.game-card-container:active {
	transform: scale(0.9, 0.9);
	box-shadow: 0px 0px 50px 20px rgba(255, 255, 255, 0.1);
  transition: 0.2s;
}

.input-group {
  background: none;
}

#navbar-search-input {
  background: none;
  border-radius: 0.3rem;
  border: none;
}

.dark-theme .content {
  background: #191919;
  transition: 0.5s ease-in-out;
}

.dark-theme .container-main {
  background: #191919;
  transition: 0.5s ease-in-out;
}

.dark-theme .home-container {
  background: #191919;
  transition: 0.5s ease-in-out;
}

.game-carousel {
  overflow: visible;
}

.dark-theme .btr-game-main-container {
  background: #191919;
  transition: 0.5s ease-in-out;
}

.avatar-restriction {
  visibility: hidden;
}

.medallion {
  transition: 0.3s;
}

.medallion:hover {
  transform: scale(1.05, 1.05)
}

.medallion:active {
  transform: scale(0.95, 0.95);
  transition: 0.2s;
}

.dropdown-menu li div {
 transition: 0.3s; 
}

li .rbx-menu-item {
   transition: 0.3s; 
}

.item-card-container {
  transition: 0.3s;
  border-radius: 1rem;
}

.item-card-thumb-container {
  border-radius: 1rem;
	draggable: false;
  align-items: center;
}

.item-card-container:hover {
	transform: scale(0.95, 0.95);
  box-shadow: 0px 0px 50px 20px rgba(0, 0, 0, 0.5);
}

.item-card-container:active {
	transform: scale(0.9, 0.9);
	box-shadow: 0px 0px 50px 20px rgba(255, 255, 255, 0.1);
  transition: 0.2s;
}

.topic {
	transition: 0.3s;
  border-radius: 1rem;
}

.topic:hover {
  transform: scale(1.05, 1.05)
}

.topic:active {
  transform: scale(0.9, 0.9)
}

.tab-pane {
  overflow: visible;
}
}
