style.css:
body {
    -moz-text-shadow: 0 0 10px #c00;
    -webkit-text-shadow: 0 0 10px #c00;
    text-shadow: 0 0 10px #ffffff;
    -webkit-user-select: #8fbcc9;
    -moz-user-select: #8fbcc9;
    user-select: #8fbcc9;
}

img {
    -moz-box-shadow:0 0 10px #c00;
    -webkit-box-shadow:0 0 10px #c00;
    box-shadow:0 0 10px #ffffff;
}

video {
    -moz-box-shadow:0 0 10px #c00;
    -webkit-box-shadow:0 0 10px #c00;
    box-shadow:0 0 10px #ffffff;
}

button {
    -moz-box-shadow:0 0 10px #c00;
    -webkit-box-shadow:0 0 10px #c00;
    box-shadow:0 0 10px #ffffff;
}
