/* ==UserStyle==
@name           Old Tumblr Dashboard (July 2023)
@namespace      github.com/openstyles/stylus
@version        6.5.0
@description    For Tumblr Update 2023
@author         Pixiel
==/UserStyle== */
@-moz-document url-prefix("https://www.tumblr.com/") {

@media (min-width: 990px) {


    /*Sticky Header*/
    .ZkG01 .h_Erh {
        z-index: 90 !important;
        position: fixed !important;
    }

    .N5wJr,
    .zmjaW {
        position: fixed !important;
        left: 54px !important;
        top: -5px !important;
        z-index: 95 !important;
        width: 480px !important;
    }

    .FtjPK .AD_w7 .JZ10N.y0ud2 {
        top: calc(55px + var(--dashboard-tabs-header-height, 0px))!important;
    }
    /*Sticky Header End*/


/*Reorders the Icons in the header

If you don't have Tumblr Live ( your header is out of order) replace the numbers with the ones listed below Inbox, Accounts, Settings, Domain and Ad-free*/

/*Home*/
ul li.g8SYn:nth-child(2) {
  order: 1;
}
/*Explore*/
ul li.g8SYn:nth-child(3) {
  order: 2;
}
/*Tumblr Live*/
ul li.g8SYn.IYrO9:nth-child(4) {
  order: 8;
Display:None;
}
/*Inbox*/
ul li.g8SYn:nth-child(7)
/*No Tumblr Live: (6)*/ {
  order: 3;
}
/*Accounts*/
ul li.g8SYn:nth-child(8)
/*No Tumblr Live: (7)*/ {
  order: 13;
}
/*Settings*/
ul li.g8SYn:nth-child(10)
/*No Tumblr Live: (9)*/ {
  order: 11;
}
/*Domain*/
ul li.g8SYn:nth-child(12)
/*No Tumblr Live: (11)*/ {
  order: 9;
Display:None;
}
/*Ad Free*/
ul li.g8SYn:nth-child(13)
/*No Tumblr Live: (12)*/ {
  order: 10;
Display:None;
}
/*Activity*/
.gM9qK > span.ZQMrc:nth-of-type(1) {
  order: 5;
}
/*Chat*/
.gM9qK > span.ZQMrc:nth-of-type(2) {
  order: 4;
}
/*Shop*/
.gM9qK > span.ZQMrc:nth-of-type(3) {
  order: 2;
}
/*Xkit*/
.tab_xkit.iconic.tab {
  order: 12;
}


/*Resizes Messenger Chat Box*/
   .j17Mp, .j17Mp.be6E9 {
    width: 400px !important;
    height: calc(100vh - 32px) !important;
    max-height: 560px !important;}


    /*Makes the Menu close (by expanding the size of the button)
This fix is a bit breaky so if you have issues let me know*/
    #settings_button.TRX6J.UyyJb[aria-expanded="true"],
    #account_button.TRX6J.UyyJb[aria-expanded="true"] {
        left: 0;
        width: 100%;
        height: 100%;
        position: fixed;
        border-bottom: 0;
        z-index: 600;
        top: 0px;
    }

    #settings_button.TRX6J.UyyJb[aria-expanded="true"] > .EvhBA.tDT48 {
        top: 6.5px;
/*If the Settings icon moves when menu is open because of Ublock, use one of the numbers below for the Right: px number - this is just cosmetic and the menu will still close*/

/*If there are none icons on the right (of the settings icon), right: 67px;*/
/*If there is one icon, right: 121px;*/
/*If there are two icons, right: 174px;*/
/*If there are three icons, right: 227px;*/
/*If there are four icons, right: 280px;*/
        right: 280px;
        position: absolute;
    }

    #account_button.TRX6J.UyyJb[aria-expanded="true"] > .EvhBA.tDT48 {
        top: 6.5px;
/*If the Accounts icon moves when menu is open because of Ublock, use one of the numbers below for the Right: px number - this is just cosmetic and the menu will still close*/

/*If there are none icons on the right (of the settings icon), right: 120px;*/
/*If there is one icon, right: 174px;*/
/*If there are two icons, right: 227px;*/
/*If there are three icons, right: 280px;*/
/*If there are four icons, right: 333px;*/
        right: 67px;
        position: absolute;
    }

    .TRX6J:focus > .EvhBA {
        outline: none;
    }






    /*Removes the weird border lines*/
    .FA5JM {
        border-right: 0;
        position: absolute;
    }

    .gPQR5.FGfuE .lSyOz {
        border-right: 0;
    }

    /*Moves the search bar*/
    .N5wJr,
    .zmjaW {
        position: absolute;
        left: -858px;
        top: -100px;
        z-index: 95;
        width: 480px;
    }

    /*Removes border under icons when menu is active*/
    .IYrO9.XuIoh {
        border-bottom: 0
    }

    /*Moves the user icons*/
    .FtjPK .AD_w7 .JZ10N.y0ud2 {
        top: calc(50px + var(--dashboard-tabs-header-height, 0px))
    }

    /*Nudges the dashboard back into place*/
    .gPQR5.ah4XU {
        max-width: 960px!important;
        margin: -7px!important;
        margin-top: 45px!important;
    }

    .ZkG01 {
        padding-top: 50px;
    }

    /*^ for masonry-view searches*/
    .gPQR5.pTaNm.ah4XU {
    max-width: 1601px!important
    }

    /*Moves the Buttons back to the Header*/
    .ZkG01 .h_Erh {
        flex-basis: 228px;
        flex-basis: 0px;
        margin-right: 0px;
        position: absolute;
        top: 0;
        height: 54px;
        width: 100%;
        border-bottom: 1px solid rgba(var(--white-on-dark), .13);
        background-color: RGB(var(--navy));
    }

    /*Creates the header bar*/
    .FA5JM {
        width: 100%;
        max-width: 100%;
    }


    /*Positions and organises the buttons*/
    .FA5JM .NkkDk .gM9qK {
        margin: 0;
        padding: 0;
        display: grid;
        grid-template-columns: auto auto auto auto auto auto auto auto auto auto auto auto auto auto;
        position: absolute;
        z-index: 90;
        right: 80px;
        justify-content: end;
        align-items: center;
        justify-items: end;
        align-content: center;
        width: 100%;
    }

    .FA5JM .NkkDk {
        padding-bottom: 0px;
        height: 54px;
        overflow-x: visible;
        overflow-y: visible;
        justify-content: center;
    }

    .IYrO9 .tDT48 {
        justify-content: flex-start;
    }

    /*Resizes the button icons & Spaces them*/
    .IYrO9 .kn4U3 svg {
        width: 22px;
        height: 21px;
    }

    .gM9qK > .IYrO9,
    .gM9qK > .ZQMrc {
        width: 53px;
    }

    .a132D {
        width: 0
    }

    .jGgIg {
        border-top: none;
        display: flex;
        top: 0;
        position: absolute;
        justify-content: flex-end;
        height: 54px;
        align-content: center;
        align-items: center;
    }

    /*Moves the notification number icons back into place*/
    .a132D > .jF6A2 > .jpu7k.jDcIV.SmqzX.D0OOd.XnKJk {
        min-width: 23px;
        height: 22px;
        border: 2px solid RGB(var(--navy))!important;
        font-size: .875rem;
        left: -22px;
        top: -9px;
        border-style: none;
        position: relative;
    }


    /*Hides the icon text*/
    .IYrO9.g8SYn .ZC1wz,
    .HHKOH {
        Display: none;
    }

.xkit--react nav #xkit_button > button > p {
        Display: none;
    }


    /*Moves the Tumblr Logo*/
    .Gav7q {
        justify-content: center;
        padding: 0px;
        display: flex;
        left: 103px;
        position: absolute;
    }

    /*Resizes the create a post button*/
    .jGgIg .ML6ef {
        display: flex;
        justify-content: center;
        align-items: center;
        margin-left: 18px;
        font-size: 0!important;
        margin-right: 2px;
        max-width: 45px;
        border-radius: 3px;
        height: 32px;
    }

    .jGgIg .ML6ef svg {
        margin-left: 7px
    }

    /*Displays Text ONLY in the menus*/
    .jL4Qq:not([hidden]) .tDT48 .a132D .ZC1wz,
    .jL4Qq:not([hidden]) .ZC1wz {
        display: contents !important
    }

    /*Adds the Spacers back into the menu and moves Log Out button*/
    .jL4Qq > .IYrO9 .lXFLr button {
        font-family: var(--font-family);
        background-color: rgba(var(--rgb-white-on-dark), 0.25);
        box-sizing: border-box;
        z-index: 1;
        justify-content: space-between;
        align-items: center;
        width: 100% !important;
        height: 30px;
        top: 0px;
        font-size: .900rem;
        color: rgba(var(--black), .65);
        font-weight: 400;
        line-height: 1.5;
        display: flex;
        flex-direction: row-reverse;
        position: absolute;
    }

  #account_subnav > li:nth-child(1) {display:block !important;}
  #account_subnav > li:nth-child(2) {display:block !important;}
  #account_subnav > li:nth-child(3) {display:block !important;}
  #account_subnav > li:nth-child(4) {display:block !important;}



    .FA5JM .NkkDk .h8SQv {
        background-color: rgba(var(--rgb-white-on-dark), 0.25);
        margin: 0 0px;
        padding: 12px 16px;
        font-size: .900rem;
        box-sizing: border-box;
        z-index: 1;
        height: 40px;
        justify-content: space-between;
        align-items: center;
        font-weight: 400;
        color: rgba(var(--black), .65) !important;
    }

    #account_subnav.jL4Qq {
        padding-top: 35px !Important;
        width: 250px;
    }

    .a132D {
        padding: 5px 0;
    }

    .kbIQf > .jpu7k .jDcIV .SmqzX .D0OOd .XnKJk {
        color: rgba(var(--black), .65);
        text-align: right;
        margin-left: 5px;
    }



    /*Moves the menu so it appears over the top and not contained in the header*/
    .jL4Qq {
        margin-bottom: 5px;
        padding: 0;
        top: 45px;
        position: fixed;
        z-index: 1000;
        overflow-x: hidden;
        border-radius: 3px;
        max-height: calc(100vh - 88px);
        overflow-y: auto;
        box-shadow: 0 0 15px rgba(0, 0, 0, .5);
    }

    /*Changes the colour of the Menus (this is set to tumblr Original Blue 
    you may need to change it with other colour pallets)*/
    .gLEkw,
    .jL4Qq > .IYrO9,
    .xstzY,
    .yElCb,
    .XstzY .kbIQf li a {
        color: inherit !important;
    }

    .KXYTk .fTJAC,
    .IYrO9 .jF6A2,
    .XstzY .kbIQf li a .dTeP6 {
        color: rgba(var(--black), .65)!important
    }

    .jL4Qq {
        background: RGB(var(--white));
        color: RGB(var(--black)) !important;
        border: 1px solid rgba(var(--white-on-dark), .13);
    }

    /*Changes menu font size*/
    .IYrO9 {
        cursor: pointer;
        font-size: 1.125rem;
        font-weight: 500;
        line-height: 1.333;
        list-style-type: none;
        display: flex;
    }

    /*Resizes the settings menu*/
    #settings_subnav:not([hidden]) {
        width: 228px;
        height: 426px;
        overflow-y: scroll;
        overflow-x: hidden;
    }


    /*Adjusts Menu's padding layout*/
    .jL4Qq > .IYrO9 .tDT48,
    .jL4Qq > .IYrO9 .lXFLr button {
        min-height: 35px;
        padding: 4px 16px;
        width: 250px;
    }


    /*Dont touch this its needed*/
    }}
