@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("www.free-tv-video-online.info") {

/* Get rid of wasted space at top */
BODY {
     margin-top: -125px !important;
     }

/* Set the default color for text */
BODY, TD {
     color: #7F7 !important;
     background-color: #222 !important;
     }

/* Set the color of titles and headings */
H1, .style3, .mnllinklist {
     color:#79D !important;
     background-color: #222 !important;
     }

/* Disable dashed focus outline */
:focus { outline: none !important; }

/* Set the speed colors to make them more readable */
.Slow {
     color: #F00 !important;
     }

.Average {
     color: #FF0 !important;
     }

.Fast {
     color: #0F0 !important;
     }

/* Set the link colors */
a, .mnllinklist a {
     color: #CCC !important;
     }

a:visited, .mnllinklist a:visited {
     color:#F5F !important;
     }

a:hover, .mnllinklist a:hover {
     color: #F90 !important;
     text-decoration: none !important;
     }
     
/* Get rid of the useless stuff */
a[href*="/download"], a[href*="http://preview.licenseacquisition.org"], .style1, IMG {
     display: none !important;
     }
}
