style.css:
body {
  background-color: black;
}

h1 {
  color: white;
  text-shadow: 0 0 5px #fff, 0 0 10px #fff, 0 0 15px #fff, 0 0 20px #0ff, 0 0 30px #0ff, 0 0 40px #0ff, 0 0 50px #0ff, 0 0 75px #0ff;
}

button {
  background-color: #0ff;
  color: black;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  box-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 15px #0ff, 0 0 20px #0ff, 0 0 30px #0ff;
  transition: all 0.3s ease-in-out;
}

button:hover {
  box-shadow: 0 0 5px #0ff, 0 0 10px #0ff, 0 0 15px #0ff, 0 0 20px #0ff, 0 0 30px #0ff, 0 0 40px #0ff, 0 0 50px #0ff, 0 0 75px #0ff;
}

body {
  filter: brightness(80%) contrast(120%);
}
