style.css:
@-moz-document domain("memrise.com") {
/* NAVBAR */
div[data-testid="header"] {
  background-color: #7B63BD;
  box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);
}
div[data-testid="classic_review-testId"] > header[data-testid="learning-session-header"]
{background: #30A0E8;}
 
div[data-testid="learn-testId"] > header[data-testid="learning-session-header"]
{background: #2ed12f;}

div[data-testid="speed_review-testId"] > header[data-testid="learning-session-header"]
{background: #f83c25;}

div[data-testid="difficult_words-testId"] > header[data-testid="learning-session-header"]
{background: #ff730b;}

header[data-testid="learning-session-header"] a {color: white;}
header[data-testid="learning-session-header"] path {fill: white;}



/* COURSE BOXSE */
div[spacing="s4"] {
	border: 1px solid #ddd;
	background-image: linear-gradient(-180deg, white 0%, rgb(243, 243, 243) 100%);
	box-shadow: -1px 2px 4px 0px rgba(0,0,0,0.16), inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,.3);
	border-radius: 15px;
	width: inherit;
	padding: 12px 12px;
	margin: 1rem 0;
}


/* BUTTONS */
a[data-testid="scb-learn"],
a[data-testid="scb-classic_review"],
a[data-testid="scb-speed_review"],
a[data-testid="scb-difficult_words"],
a[data-testid="scb-video"],
a[data-testid="scb-listening_skills"],
div[data-testid="categoryReviewButton"] a
{
	border-radius: 10px;
	color: snow;
}
a[data-testid="scb-learn"] {background-image: linear-gradient(-180deg, #8fff24 0%, #15b43d 100%);}
a[data-testid="scb-classic_review"] {background-image: linear-gradient(-180deg, #5fccff 0%, #30A0E8 100%);}
a[data-testid="scb-speed_review"] {background-image: linear-gradient(-180deg, #ff7f6e, #f83c25);}
a[data-testid="scb-difficult_words"] {background-image: linear-gradient(-180deg, #ffa45e, #F77C20);}
a[data-testid="scb-video"] {background-image: linear-gradient(-180deg, #ffcf4a, #ffbb00);}
a[data-testid="scb-listening_skills"] {background-image: linear-gradient(-180deg, #ec87fd, #b84eca);}
div[data-testid="categoryReviewButton"] a {background: linear-gradient(-180deg, #5fccff 0%, #30A0E8 100%);}

a[data-testid="scb-learn"]:hover,
a[data-testid="scb-classic_review"]:hover,
a[data-testid="scb-speed_review"]:hover,
a[data-testid="scb-difficult_words"]:hover,
a[data-testid="scb-video"]:hover,
div[data-testid="categoryReviewButton"] a:hover
{
  filter: brightness(1.1);
  color: white !important;
}


/* MODE BUTTON */
button[title="Mode selector"] {background-color: #bbb;}
circle {fill: white;}
button[title="Mode selector"]:hover {
  background-color: #ccc;
}


/* COURSE PROGRESS */
div[color="yellow"] {
  /*background-image: linear-gradient(#b2ec2a, #15b43d);*/
  background-image: repeating-linear-gradient(90deg,    hotpink 0px, hotpink 3px, pink 9px, #eee 10px);
}


/* SVGs */
a path {
  fill: white !important;
}


/* ==================== REVIEW / QUIZ ===================== */

/* CHOICE BUTTON*/
div[data-testid="testLearnableCard"] button {
  border-radius: 10px;
	border: 1px solid #eee;
  box-shadow: inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,0.3);
}
div[data-testid="testLearnableCard"] button:hover,
div[data-testid="testLearnableCard"] button:focus {
 transform: translateY(-1px);
 background-color: #ddf2ff;
  border: none;
 box-shadow:  inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,0.3);
}
div[data-testid="testLearnableCard"] button div:first-child {
  background: #ddd;
}
}
