style.css:
@-moz-document domain("memrise.com") {
/* NAVBAR */
div[data-testid="header"] {
  background-color: #7B63BD;
  box-shadow: 0 2px 4px 0 rgba(0,0,0,0.2);
}


/* COURSE BOXSE */
div[spacing="s4"] {
	border: 1px solid var(--gray);
	background-image: linear-gradient(-180deg, white 0%, rgb(243, 243, 243) 100%);
	box-shadow: -1px 2px 4px 0px rgba(0,0,0,0.16), inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,.3);
	border-radius: 15px;
	width: inherit;
	padding: 12px 12px;
	margin: 1rem 0;
}

/* BUTTONS */
a[data-testid="scb-learn"],
a[data-testid="scb-classic_review"],
a[data-testid="scb-speed_review"],
a[data-testid="scb-difficult_words"],
a[data-testid="scb-video"]
{
	border-radius: 10px;
	color: snow;
 	border-width: 2px;
	border-style: solid;
  box-shadow: -1px 2px 4px 0px rgba(0,0,0,0.16), inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,0.3);
}
a[data-testid="scb-learn"] {
  background-image: linear-gradient(-180deg, #8fff24 0%, #15b43d 100%);
	border-color: #15b43d;
}
a[data-testid="scb-classic_review"] {
	background-image: linear-gradient(-180deg, #5fccff 0%, #30A0E8 100%);
	border-color: #30A0E8;
}
a[data-testid="scb-speed_review"] {
	background-image: linear-gradient(-180deg, #ff7f6e, #f83c25);
	border-color: #f83c25;
}
a[data-testid="scb-difficult_words"] {
	background-image: linear-gradient(-180deg, #ffa45e, #F77C20);
	border-color: #F77C20;
}
a[data-testid="scb-video"] {
	background-image: linear-gradient(-180deg, #ffcf4a, #ffbb00);
	border-color: #ffbb00;
}
a[data-testid="scb-learn"]:hover,
a[data-testid="scb-classic_review"]:hover,
a[data-testid="scb-speed_review"]:hover,
a[data-testid="scb-difficult_words"]:hover,
a[data-testid="scb-video"]:hover
{
  filter: brightness(1.1);
  color: white !important;
}


/* COURSE PROGRESS */
div[color="yellow"] {
  background-image: linear-gradient(#b2ec2a, #15b43d);
}


/* SVGs */
a path {
  fill: white !important;
  }
  
  
/* CHOICE BUTTON*/
button[type="button"] {
  border-radius: 10px;
	border: 2px solid gray;
  box-shadow: -1px 2px 4px 0px rgba(0,0,0,0.16), inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,0.3);
}
button[type="button"]:hover {
transform: translateY(-1px);
  background-color: #eee;
    box-shadow: -3px 4px 11px 0px rgba(0,0,0,0.16), inset 0px -2px 7px 0px rgba(0,0,0,0.1), inset 0px 2px 0px 0px rgba(255,255,255,0.3);
}
}
